import React, { useState, useEffect } from 'react';
import { 
  Home, 
  Phone, 
  Mail, 
  MapPin, 
  CheckCircle, 
  ArrowRight, 
  ArrowLeft,
  User,
  Building,
  FileText,
  DollarSign
} from 'lucide-react';

const PropertyManagementSite = () => {
  console.log('PropertyManagementSite component rendering');

  const [currentStep, setCurrentStep] = useState('home');
  const [formData, setFormData] = useState({
    guestCard: {},
    additionalQuestions: {},
    ownerApplication: {}
  });
  const [isLoading, setIsLoading] = useState(false);

  const updateFormData = (step, data) => {
    setFormData(prev => ({
      ...prev,
      [step]: { ...prev[step], ...data }
    }));
  };

  const nextStep = (step) => {
    setCurrentStep(step);
  };

  const prevStep = () => {
    const steps = ['home', 'additionalQuestions', 'ownerApplication', 'success'];
    const currentIndex = steps.indexOf(currentStep);
    if (currentIndex > 0) {
      setCurrentStep(steps[currentIndex - 1]);
    }
  };

  const resetApplication = () => {
    setCurrentStep('home');
    setFormData({
      guestCard: {},
      additionalQuestions: {},
      ownerApplication: {}
    });
  };

  // Hero Form Component
  const HeroForm = () => {
    const [address, setAddress] = useState('');

    const handleSubmit = (e) => {
      e.preventDefault();
      if (address.trim()) {
        setIsLoading(true);
        setTimeout(() => {
          alert('Thank you! We will contact you within 24 hours to discuss your property analysis.');
          setAddress('');
          setIsLoading(false);
        }, 1500);
      }
    };

    return (
      <section className="hero-section">
        <div className="hero-content">
          <h1>GET YOUR FREE RENTAL PRICE ANALYSIS</h1>
          <p>Call us today at 843-994-3869 or enter your address below.</p>
          <form onSubmit={handleSubmit} className="hero-form">
            <input
              type="text"
              placeholder="Enter your property address here*"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
              className="address-input"
            />
            <button type="submit" className="submit-btn" disabled={isLoading}>
              {isLoading ? 'Processing...' : 'SUBMIT'}
            </button>
          </form>
        </div>
      </section>
    );
  };

  // Guest Card Form Component
  const GuestCardForm = () => {
    const [formState, setFormState] = useState({
      // Lead Source Information
      howDidYouHear: '',
      referralSource: '',
      internetSource: '',

      // Owner Contact Information
      fullName: '',
      email: '',
      phone: ''
    });

