sourcesContent:
import React, { useState, useEffect } from 'react';
import { 
  Home, 
  Phone, 
  Mail, 
  MapPin, 
  CheckCircle, 
  ArrowRight, 
  ArrowLeft,
  User,
  Building,
  FileText,
  DollarSign
} from 'lucide-react';

const PropertyManagementSite = () => {
  console.log('PropertyManagementSite component rendering');

  const [currentStep, setCurrentStep] = useState('home');
  const [formData, setFormData] = useState({
    guestCard: {},
    additionalQuestions: {},
    ownerApplication: {}
  });
  const [isLoading, setIsLoading] = useState(false);

  const updateFormData = (step, data) => {
    setFormData(prev => ({
      ...prev,
      [step]: { ...prev[step], ...data }
    }));
  };

  const nextStep = (step) => {
    setCurrentStep(step);
  };

  const prevStep = () => {
    const steps = ['home', 'additionalQuestions', 'ownerApplication', 'success'];
    const currentIndex = steps.indexOf(currentStep);
    if (currentIndex > 0) {
      setCurrentStep(steps[currentIndex - 1]);
    }
  };

  const resetApplication = () => {
    setCurrentStep('home');
    setFormData({
      guestCard: {},
      additionalQuestions: {},
      ownerApplication: {}
    });
  };

  // Hero Form Component
  const HeroForm = () => {
    const [address, setAddress] = useState('');

    const handleSubmit = (e) => {
      e.preventDefault();
      if (address.trim()) {
        setIsLoading(true);
        setTimeout(() => {
          alert('Thank you! We will contact you within 24 hours to discuss your property analysis.');
          setAddress('');
          setIsLoading(false);
        }, 1500);
      }
    };

    return (
      <section className="hero-section">
        <div className="hero-content">
          <h1>GET YOUR FREE RENTAL PRICE ANALYSIS</h1>
          <p>Call us today at 843-994-3869 or enter your address below.</p>
          <form onSubmit={handleSubmit} className="hero-form">
            <input
              type="text"
              placeholder="Enter your property address here*"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
              className="address-input"
            />
            <button type="submit" className="submit-btn" disabled={isLoading}>
              {isLoading ? 'Processing...' : 'SUBMIT'}
            </button>
          </form>
        </div>
      </section>
    );
  };

  // Guest Card Form Component
  const GuestCardForm = () => {
    const [formState, setFormState] = useState({
      firstName: '',
      lastName: '',
      email: '',
      phone: '',
      propertyAddress: ''
    });

    const handleInputChange = (e) => {
      const { name, value } = e.target;
      setFormState(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = (e) => {
      e.preventDefault();

      // Validation
      if (!formState.firstName || !formState.lastName || !formState.email || !formState.phone) {
        alert('Please fill in all required fields.');
        return;
      }

      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      if (!emailRegex.test(formState.email)) {
        alert('Please enter a valid email address.');
        return;
      }

      updateFormData('guestCard', formState);
      nextStep('additionalQuestions');
    };

    return (
      <div className="guest-card-form">
        <h3>Start Your Application</h3>
        <p className="form-description">Begin by creating your guest card with basic information.</p>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>I am a: *</label>
            <div className="radio-group">
              {[
                { value: 'owner', label: 'Property Owner' },
                { value: 'renter', label: 'Potential Renter' },
                { value: 'vendor', label: 'Service Vendor' }
              ].map(option => (
                <label key={option.value} className="radio-label">
                  <input
                    type="radio"
                    name="userType"
                    value={option.value}
                    checked={formState.userType === option.value}
                    onChange={handleInputChange}
                  />
                  <span className="radio-custom"></span>
                  {option.label}
                </label>
              ))}
            </div>
          </div>

          <div className="form-group">
            <input
              type="text"
              name="firstName"
              placeholder="First Name *"
              value={formState.firstName}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <input
              type="text"
              name="lastName"
              placeholder="Last Name *"
              value={formState.lastName}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <input
              type="email"
              name="email"
              placeholder="Email Address *"
              value={formState.email}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <input
              type="tel"
              name="phone"
              placeholder="Phone Number *"
              value={formState.phone}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <input
              type="text"
              name="propertyAddress"
              placeholder="Property Address (if applicable)"
              value={formState.propertyAddress}
              onChange={handleInputChange}
            />
          </div>

          <button type="submit" className="form-submit-btn">
            Continue to Application <ArrowRight size={16} />
          </button>
        </form>
      </div>
    );
  };

  // Progress Bar Component
  const ProgressBar = ({ currentStep }) => {
    const steps = [
      { id: 'home', label: '1', title: 'Guest Card' },
      { id: 'additionalQuestions', label: '2', title: 'Additional Info' },
      { id: 'ownerApplication', label: '3', title: 'Application' }
    ];

    const getCurrentStepIndex = () => {
      return steps.findIndex(step => step.id === currentStep);
    };

    const currentIndex = getCurrentStepIndex();

    return (
      <div className="progress-bar">
        {steps.map((step, index) => (
          <React.Fragment key={step.id}>
            <div className={`progress-step ${index <= currentIndex ? 'completed' : ''} ${index === currentIndex ? 'active' : ''}`}>
              {step.label}
            </div>
            {index < steps.length - 1 && (
              <div className={`progress-line ${index < currentIndex ? 'completed' : ''}`}></div>
            )}
          </React.Fragment>
        ))}
      </div>
    );
  };

  // Additional Questions Component
  const AdditionalQuestions = () => {
    const [formState, setFormState] = useState({
      referralSource: '',
      contactMethod: '',
      propertyType: [],
      goals: ''
    });


    const handleInputChange = (e) => {
      const { name, value, type, checked } = e.target;

      if (type === 'checkbox') {
        setFormState(prev => ({
          ...prev,
          [name]: checked 
            ? [...prev[name], value]
            : prev[name].filter(item => item !== value)
        }));
      } else {
        setFormState(prev => ({ ...prev, [name]: value }));
      }
    };

    const handleSubmit = (e) => {
      e.preventDefault();

      if (!formState.referralSource || !formState.contactMethod) {
        alert('Please fill in all required fields.');
        return;
      }

      updateFormData('additionalQuestions', formState);
      nextStep('ownerApplication');
    };

    return (
      <div className="application-screen">
        <div className="application-content">
          <ProgressBar currentStep={currentStep} />

          <h2>Additional Information</h2>
          <p className="screen-description">Please answer these additional questions to help us better understand your needs.</p>

          <form onSubmit={handleSubmit}>
            <div className="form-row">
              <div className="form-group">
                <label>How did you hear about us? *</label>
                <select name="referralSource" value={formState.referralSource} onChange={handleInputChange} required>
                  <option value="">Select an option</option>
                  <option value="google">Google Search</option>
                  <option value="facebook">Facebook</option>
                  <option value="referral">Referral from friend/family</option>
                  <option value="realtor">Real Estate Agent</option>
                  <option value="website">Our Website</option>
                  <option value="other">Other</option>
                </select>
              </div>

              <div className="form-group">
                <label>Preferred contact method *</label>
                <select name="contactMethod" value={formState.contactMethod} onChange={handleInputChange} required>
                  <option value="">Select preferred method</option>
                  <option value="phone">Phone Call</option>
                  <option value="email">Email</option>
                  <option value="text">Text Message</option>
                </select>
              </div>
            </div>

            <div className="form-group">
              <label>What type of property do you own? (if applicable)</label>
              <div className="checkbox-group">
                {[
                  { value: 'single-family', label: 'Single Family Home' },
                  { value: 'condo', label: 'Condominium' },
                  { value: 'townhouse', label: 'Townhouse' },
                  { value: 'duplex', label: 'Duplex' }
                ].map(option => (
                  <label key={option.value} className="checkbox-label">
                    <input
                      type="checkbox"
                      name="propertyType"
                      value={option.value}
                      checked={formState.propertyType.includes(option.value)}
                      onChange={handleInputChange}
                    />
                    <span className="checkbox-custom"></span>
                    {option.label}
                  </label>
                ))}
              </div>
            </div>

            <div className="form-group">
              <label>Tell us about your property management goals</label>
              <textarea
                name="goals"
                placeholder="What are you hoping to achieve with property management services?"
                value={formState.goals}
                onChange={handleInputChange}
                rows="4"
              />
            </div>

            <div className="form-actions">
              <button type="button" className="btn-secondary" onClick={prevStep}>
                <ArrowLeft size={16} /> Back
              </button>
              <button type="submit" className="btn-primary">
                Continue to Owner Application <ArrowRight size={16} />
              </button>
            </div>
          </form>
        </div>
      </div>
    );
  };

  // Owner Application Component
  const OwnerApplication = () => {
    const [formState, setFormState] = useState({
      propertyAddress: '',
      propertyType: '',
      bedrooms: '',
      bathrooms: '',
      squareFootage: '',
      yearBuilt: '',
      occupancyStatus: '',
      rentRange: '',
      features: [],
      specialRequirements: ''
    });


    const handleInputChange = (e) => {
      const { name, value, type, checked } = e.target;

      if (type === 'checkbox') {
        setFormState(prev => ({
          ...prev,
          [name]: checked 
            ? [...prev[name], value]
            : prev[name].filter(item => item !== value)
        }));
      } else {
        setFormState(prev => ({ ...prev, [name]: value }));
      }
    };

    const handleSubmit = (e) => {
      e.preventDefault();

      if (!formState.propertyAddress || !formState.propertyType || !formState.bedrooms || !formState.bathrooms || !formState.occupancyStatus) {
        alert('Please fill in all required fields.');
        return;
      }

      updateFormData('ownerApplication', formState);
      nextStep('success');
    };

    return (
      <div className="application-screen">
        <div className="application-content">
          <ProgressBar currentStep={currentStep} />

          <h2>Owner Application</h2>
          <p className="screen-description">Complete your owner application with detailed property information.</p>

          <form onSubmit={handleSubmit}>
            <div className="form-section">
              <h3>Property Details</h3>

              <div className="form-row">
                <div className="form-group">
                  <label>Property Address *</label>
                  <input
                    type="text"
                    name="propertyAddress"
                    placeholder="123 Main Street, City, State ZIP"
                    value={formState.propertyAddress}
                    onChange={handleInputChange}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Property Type *</label>
                  <select name="propertyType" value={formState.propertyType} onChange={handleInputChange} required>
                    <option value="">Select property type</option>
                    <option value="single-family">Single Family Home</option>
                    <option value="condo">Condominium</option>
                    <option value="townhouse">Townhouse</option>
                    <option value="duplex">Duplex</option>
                    <option value="multi-family">Multi-Family (3+ units)</option>
                  </select>
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Number of Bedrooms *</label>
                  <input
                    type="number"
                    name="bedrooms"
                    min="1"
                    max="10"
                    value={formState.bedrooms}
                    onChange={handleInputChange}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Number of Bathrooms *</label>
                  <input
                    type="number"
                    name="bathrooms"
                    min="1"
                    max="10"
                    step="0.5"
                    value={formState.bathrooms}
                    onChange={handleInputChange}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Square Footage</label>
                  <input
                    type="number"
                    name="squareFootage"
                    placeholder="e.g., 1500"
                    value={formState.squareFootage}
                    onChange={handleInputChange}
                  />
                </div>
              </div>

              <div className="form-group">
                <label>Year Built</label>
                <input
                  type="number"
                  name="yearBuilt"
                  min="1800"
                  max="2024"
                  value={formState.yearBuilt}
                  onChange={handleInputChange}
                />
              </div>
            </div>

            <div className="form-section">
              <h3>Current Status</h3>

              <div className="form-group">
                <label>Current occupancy status *</label>
                <div className="radio-group">
                  {[
                    { value: 'occupied', label: 'Currently Occupied' },
                    { value: 'vacant', label: 'Currently Vacant' },
                    { value: 'renovation', label: 'Under Renovation' }
                  ].map(option => (
                    <label key={option.value} className="radio-label">
                      <input
                        type="radio"
                        name="occupancyStatus"
                        value={option.value}
                        checked={formState.occupancyStatus === option.value}
                        onChange={handleInputChange}
                        required
                      />
                      <span className="radio-custom"></span>
                      {option.label}
                    </label>
                  ))}
                </div>
              </div>

              <div className="form-group">
                <label>Expected monthly rent range</label>
                <select name="rentRange" value={formState.rentRange} onChange={handleInputChange}>
                  <option value="">Select range</option>
                  <option value="under-1000">Under $1,000</option>
                  <option value="1000-1500">$1,000 - $1,500</option>
                  <option value="1500-2000">$1,500 - $2,000</option>
                  <option value="2000-2500">$2,000 - $2,500</option>
                  <option value="2500-3000">$2,500 - $3,000</option>
                  <option value="over-3000">Over $3,000</option>
                </select>
              </div>
            </div>

            <div className="form-section">
              <h3>Additional Information</h3>

              <div className="form-group">
                <label>Property features (check all that apply)</label>
                <div className="checkbox-group">
                  {[
                    { value: 'garage', label: 'Garage' },
                    { value: 'pool', label: 'Pool' },
                    { value: 'yard', label: 'Yard/Garden' },
                    { value: 'pet-friendly', label: 'Pet Friendly' },
                    { value: 'furnished', label: 'Furnished' },
                    { value: 'utilities-included', label: 'Utilities Included' }
                  ].map(option => (
                    <label key={option.value} className="checkbox-label">
                      <input
                        type="checkbox"
                        name="features"
                        value={option.value}
                        checked={formState.features.includes(option.value)}
                        onChange={handleInputChange}
                      />
                      <span className="checkbox-custom"></span>
                      {option.label}
                    </label>
                  ))}
                </div>
              </div>

              <div className="form-group">
                <label>Any special requirements or notes?</label>
                <textarea
                  name="specialRequirements"
                  placeholder="Any specific requirements, restrictions, or additional information about your property..."
                  value={formState.specialRequirements}
                  onChange={handleInputChange}
                  rows="4"
                />
              </div>
            </div>

            <div className="form-actions">
              <button type="button" className="btn-secondary" onClick={prevStep}>
                <ArrowLeft size={16} /> Back
              </button>
              <button type="submit" className="btn-primary">
                Submit Application <CheckCircle size={16} />
              </button>
            </div>
          </form>
        </div>
      </div>
    );
  };

  // Success Screen Component
  const SuccessScreen = () => {

    return (
      <div className="application-screen">
        <div className="application-content success-content">
          <div className="success-icon">
            <CheckCircle size={80} color="#10b981" />
          </div>

          <h2>Application Submitted Successfully!</h2>
          <p className="success-message">
            Thank you for your interest in Premier Property Management. We have received your application and will review it carefully.
          </p>

          <div className="next-steps">
            <h3>What happens next?</h3>
            <ul>
              <li>Our team will review your application within 24 hours</li>
              <li>We'll contact you to schedule a property evaluation</li>
              <li>We'll provide you with a customized management proposal</li>
              <li>You'll receive our comprehensive service agreement</li>
            </ul>
          </div>

          <div className="contact-info">
            <p><strong>Questions?</strong> Contact us at 843-278-9003 or email info@premierpm.com</p>
          </div>

          <button type="button" className="btn-primary" onClick={resetApplication}>
            Start New Application
          </button>
        </div>
      </div>
    );
  };

  // Navigation function
  const goToPortal = () => {
    window.history.pushState({}, '', '/');
    window.dispatchEvent(new PopStateEvent('popstate'));
  };

  // Main render logic
  if (currentStep === 'home') {
    return (
      <div className="property-management-site" style={{ 
        width: '100%', 
        minHeight: '100vh',
        margin: 0,
        padding: 0,
        fontFamily: 'Inter, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif'
      }}>
        {/* Top Bar */}
        <div className="top-bar">
          <div className="container">
            <div className="top-bar-left">
              <button onClick={goToPortal} style={{ 
                background: 'none', 
                border: 'none', 
                color: 'white', 
                cursor: 'pointer',
                textDecoration: 'underline',
                fontSize: '14px'
              }}>
                 Back to Portal
              </button>
              <span>|</span>
              <a href="#">OWNER LOGIN</a>
              <span>|</span>
              <a href="#">PAY RENT</a>
              <span>|</span>
              <a href="#">REPAIR REQUEST</a>
            </div>
            <div className="top-bar-right">
              <Phone size={16} />
              <span>843-278-9003</span>
            </div>
          </div>
        </div>

        {/* Main Navigation */}
        <nav className="main-nav">
          <div className="container">
            <div className="nav-left">
              <div className="logo">
                <div className="logo-icon">P</div>
                <div className="logo-text">
                  <div className="logo-main">PREMIER</div>
                  <div className="logo-sub">PROPERTY MANAGEMENT</div>
                </div>
              </div>
            </div>
            <div className="nav-right">
              <a href="#" className="nav-link">HOME</a>
              <a href="#" className="nav-link active">OWNERS & INVESTORS</a>
              <a href="#" className="nav-link">RENTALS</a>
              <a href="#" className="nav-link">RESIDENTS</a>
              <a href="#" className="nav-link">AGENTS</a>
              <a href="#" className="nav-link">ABOUT US</a>
            </div>
          </div>
        </nav>

        {/* Hero Section */}
        <HeroForm />

        {/* Main Content */}
        <main className="main-content">
          <div className="container">
            <div className="content-grid">
              {/* Left Column */}
              <div className="left-column">
                <h2 className="section-title">
                  <span className="title-accent"></span>
                  PROPERTY MANAGEMENT CHARLESTON
                </h2>
                <h3 className="section-subtitle">LET US MANAGE YOUR CHARLESTON RENTAL PROPERTY</h3>

                <div className="team-image">
                  <img src="https://images.unsplash.com/photo-1560472354-b33ff0c44a43?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80" alt="Professional team at work" />
                </div>

                <div className="content-text">
                  <p>At Premier Property Management, we specialize in maximizing your rental property's potential while minimizing your stress. Our experienced team handles everything from tenant screening to maintenance coordination, ensuring your investment works for you.</p>

                  <h4>Why Choose Premier Property Management?</h4>
                  <ul>
                    <li>Comprehensive tenant screening and placement</li>
                    <li>24/7 maintenance support</li>
                    <li>Transparent financial reporting</li>
                    <li>Local market expertise</li>
                    <li>Competitive management fees</li>
                  </ul>
                </div>
              </div>

              {/* Right Column - Guest Card Form */}
              <div className="right-column">
                <GuestCardForm />
              </div>
            </div>
          </div>
        </main>

        {/* Footer */}
        <footer className="footer">
          <div className="container">
            <div className="footer-content">
              <div className="footer-section">
                <h4>Contact Information</h4>
                <p><Phone size={16} /> 843-278-9003</p>
                <p><Mail size={16} /> info@premierpm.com</p>
                <p><MapPin size={16} /> 123 King Street, Charleston, SC 29401</p>
              </div>
              <div className="footer-section">
                <h4>Services</h4>
                <ul>
                  <li>Property Management</li>
                  <li>Tenant Screening</li>
                  <li>Maintenance Coordination</li>
                  <li>Financial Reporting</li>
                </ul>
              </div>
              <div className="footer-section">
                <h4>Quick Links</h4>
                <ul>
                  <li><a href="#">Owner Portal</a></li>
                  <li><a href="#">Tenant Portal</a></li>
                  <li><a href="#">Maintenance Request</a></li>
                  <li><a href="#">Rent Payment</a></li>
                </ul>
              </div>
            </div>
            <div className="footer-bottom">
              <p>&copy; 2024 Premier Property Management. All rights reserved.</p>
            </div>
          </div>
        </footer>
      </div>
    );
  }

  // Render application flow screens
  switch (currentStep) {
    case 'additionalQuestions':
      return <AdditionalQuestions />;
    case 'ownerApplication':
      return <OwnerApplication />;
    case 'success':
      return <SuccessScreen />;
    default:
      return null;
  }
};

export default PropertyManagementSite;
