import React, { useState } from 'react';
import { 
  CheckCircle, 
  Clock, 
  AlertCircle, 
  User, 
  Users as UsersIcon, 
  Filter,
  ChevronDown,
  CheckSquare,
  Square,
  Calendar,
  Bot,
  X,
  XCircle,
  ExternalLink,
  UserCheck,
  Snooze,
  MessageSquare,
  Mail,
  Phone,
  Home,
  Circle
} from 'lucide-react';
import { mockTasks, currentUser, mockLeads } from '../mockData';

const TasksWidget = ({ onNavigateToLead }) => {
  const [filter, setFilter] = useState('all'); // all, me, me-others, specific, ai
  const [showFilterMenu, setShowFilterMenu] = useState(false);
  const [selectedTasks, setSelectedTasks] = useState(new Set());
  const [selectedTask, setSelectedTask] = useState(null);
  const [showTaskDetail, setShowTaskDetail] = useState(false);
  const [showCompletionModal, setShowCompletionModal] = useState(false);
  const [completionOutcome, setCompletionOutcome] = useState(null); // 'answered', 'no-answer'
  const [completionNote, setCompletionNote] = useState('');
  const [showPastTasks, setShowPastTasks] = useState(true);

  // Filter tasks based on current filter
  const getFilteredTasks = () => {
    let filtered = mockTasks;

    switch (filter) {
      case 'me':
        filtered = mockTasks.filter(t => t.assignedTo === currentUser.name);
        break;
      case 'me-others':
        filtered = mockTasks.filter(t => 
          t.assignedTo === currentUser.name || t.assignedTo === null
        );
        break;
      case 'unassigned':
        filtered = mockTasks.filter(t => t.assignedTo === null);
        break;
      case 'ai':
        // Mock: no AI tasks yet
        filtered = [];
        break;
      default:
        filtered = mockTasks.filter(t => t.status !== 'completed');
    }

    return filtered.filter(t => t.status !== 'completed');
  };

  const filteredTasks = getFilteredTasks();

