import React, { useState } from 'react';
import { Play, Pause, Edit, Trash2, ExternalLink, Zap, Clock, CheckCircle, AlertCircle, Folder, FolderOpen, Plus, ChevronRight, ChevronDown, MoreVertical, FolderPlus, FolderEdit, FolderX, X, Database, Settings, Save, RefreshCw, Users, Eye, GripVertical, ToggleLeft, ToggleRight } from 'lucide-react';
import VisualWorkflowBuilder from './VisualWorkflowBuilder';

const AutomationsTab = () => {
  const [activeFilter, setActiveFilter] = useState('all');
  const [expandedFolders, setExpandedFolders] = useState(new Set(['system', 'lead-management', 'customer-service']));
  const [showCreateFolder, setShowCreateFolder] = useState(false);
  const [newFolderName, setNewFolderName] = useState('');
  const [newFolderParent, setNewFolderParent] = useState(null);
  const [editingFolder, setEditingFolder] = useState(null);
  const [editingFolderName, setEditingFolderName] = useState('');
  const [editingWorkflow, setEditingWorkflow] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showWorkflowBuilder, setShowWorkflowBuilder] = useState(false);
  const [editingWorkflowId, setEditingWorkflowId] = useState(null);
  const [selectedFolderForNew, setSelectedFolderForNew] = useState(null);

  // Owner Application Settings State
  const [showOwnerSettings, setShowOwnerSettings] = useState(false);
  const [showApplicationsView, setShowApplicationsView] = useState(false);
  const [showQuestionModal, setShowQuestionModal] = useState(false);
  const [selectedApplication, setSelectedApplication] = useState(null);
  const [ownerSettings, setOwnerSettings] = useState({
    customQuestions: [
      {
        id: 'q1',
        label: 'How many properties do you currently own?',
        type: 'number',
        weight: 0.3,
        options: [],
        order: 1,
        required: false,
        active: true
      },
