import React, { useState, useEffect } from 'react';
import { 
  Plus,
  Trash2,
  Edit,
  GripVertical,
  Type,
  Hash,
  List,
  CheckSquare,
  Calendar,
  Mail,
  Phone,
  FileText,
  ChevronDown,
  ChevronUp,
  Save,
  Eye,
  ArrowLeft,
  AlertCircle
} from 'lucide-react';
import { mockLeadQuestionnaireForm, mockOnboardingForm } from '../mockData';

const FormsBuilder = ({ formType = 'lead-questionnaire', onBack }) => {
  const [form, setForm] = useState(null);
  const [activeSection, setActiveSection] = useState(null);
  const [previewMode, setPreviewMode] = useState(false);
  const [hasChanges, setHasChanges] = useState(false);

  // Load form data on mount
  useEffect(() => {
    const formData = formType === 'lead-questionnaire' 
      ? JSON.parse(JSON.stringify(mockLeadQuestionnaireForm))
      : JSON.parse(JSON.stringify(mockOnboardingForm));

    setForm(formData);
    if (formData.sections.length > 0) {
      setActiveSection(formData.sections[0].id);
    }
  }, [formType]);

  const fieldTypes = [
    { value: 'text', label: 'Text', icon: Type },
    { value: 'textarea', label: 'Text Area', icon: FileText },
    { value: 'number', label: 'Number', icon: Hash },
    { value: 'email', label: 'Email', icon: Mail },
    { value: 'phone', label: 'Phone', icon: Phone },
    { value: 'select', label: 'Dropdown', icon: List },
    { value: 'checkbox', label: 'Checkbox', icon: CheckSquare },
    { value: 'date', label: 'Date', icon: Calendar }
  ];

  const isLeadQuestionnaire = formType === 'lead-questionnaire';

  const addSection = () => {
    const newSection = {
      id: `section-${Date.now()}`,
      title: 'New Section',
      order: form.sections.length + 1,
      fields: []
    };
    setForm({
      ...form,
      sections: [...form.sections, newSection]
    });
    setActiveSection(newSection.id);
    setHasChanges(true);
  };

  const deleteSection = (sectionId) => {
    if (form.sections.length <= 1) {
      alert('You must have at least one section');
      return;
    }

    const newSections = form.sections.filter(s => s.id !== sectionId);
    setForm({
      ...form,
      sections: newSections
    });

    if (activeSection === sectionId && newSections.length > 0) {
      setActiveSection(newSections[0].id);
    }
    setHasChanges(true);
  };

  const updateSectionTitle = (sectionId, title) => {
    setForm({
      ...form,
      sections: form.sections.map(s =>
        s.id === sectionId ? { ...s, title } : s
      )
    });
    setHasChanges(true);
  };

  const addField = (fieldType = 'text') => {
    const section = form.sections.find(s => s.id === activeSection);
    const newField = {
      id: `field-${Date.now()}`,
      label: 'New Field',
      type: fieldType,
      required: isLeadQuestionnaire ? false : false, // Default to false
      order: section.fields.length + 1,
      helpText: '',
      options: (fieldType === 'select' || fieldType === 'checkbox') ? [] : undefined
    };

    setForm({
      ...form,
      sections: form.sections.map(s =>
        s.id === activeSection
          ? { ...s, fields: [...s.fields, newField] }
          : s
      )
    });
    setHasChanges(true);
  };

  const updateField = (sectionId, fieldId, updates) => {
    setForm({
      ...form,
      sections: form.sections.map(s =>
        s.id === sectionId
          ? {
              ...s,
              fields: s.fields.map(f =>
                f.id === fieldId ? { ...f, ...updates } : f
              )
            }
          : s
      )
    });
    setHasChanges(true);
  };

  const deleteField = (sectionId, fieldId) => {
    setForm({
      ...form,
      sections: form.sections.map(s =>
        s.id === sectionId
          ? { ...s, fields: s.fields.filter(f => f.id !== fieldId) }
          : s
      )
    });
    setHasChanges(true);
  };

  const saveForm = () => {
    // In a real app, this would save to backend
    console.log('Saving form:', form);
    alert('Form saved successfully! (Mock save - no backend)');
    setHasChanges(false);
  };

  if (!form) {
    return <div className="loading">Loading form...</div>;
  }

  const currentSection = form.sections.find(s => s.id === activeSection);

  const FieldEditor = ({ field, sectionId }) => {
    const [expanded, setExpanded] = useState(false);

    const handleOptionsChange = (value) => {
      const optionsArray = value.split('\n').filter(opt => opt.trim() !== '');
      updateField(sectionId, field.id, { options: optionsArray });
    };

    return (
      <div className="field-editor">
        <div className="field-editor-header">
          <button className="field-drag-handle" title="Drag to reorder">
            <GripVertical size={16} />
          </button>
          <div className="field-info">
            <span className="field-label">{field.label}</span>
            <span className="field-type">{field.type}</span>
            {field.required && <span className="field-required-badge">Required</span>}
          </div>
          <div className="field-actions">
            <button
              className="btn-icon"
              onClick={() => setExpanded(!expanded)}
              title={expanded ? "Collapse" : "Expand"}
            >
              {expanded ? <ChevronUp size={16} /> : <ChevronDown size={16} />}
            </button>
            <button
              className="btn-icon"
              onClick={() => deleteField(sectionId, field.id)}
              title="Delete field"
            >
              <Trash2 size={16} />
            </button>
          </div>
        </div>

        {expanded && (
          <div className="field-editor-content">
            <div className="form-group">
              <label>Field Label</label>
              <input
                type="text"
                className="form-input"
                value={field.label}
                onChange={(e) => updateField(sectionId, field.id, { label: e.target.value })}
              />
            </div>

            <div className="form-group">
              <label>Field Type</label>
              <select
                className="form-select"
                value={field.type}
                onChange={(e) => {
                  const newType = e.target.value;
                  const updates = { type: newType };

                  // Add or remove options based on type
                  if (newType === 'select' || newType === 'checkbox') {
                    if (!field.options) {
                      updates.options = [];
                    }
                  } else {
                    updates.options = undefined;
                  }

                  updateField(sectionId, field.id, updates);
                }}
              >
                {fieldTypes.map(type => (
                  <option key={type.value} value={type.value}>
                    {type.label}
                  </option>
                ))}
              </select>
            </div>

            {(field.type === 'select' || field.type === 'checkbox') && (
              <div className="form-group">
                <label>Options (one per line)</label>
                <textarea
                  className="form-input"
                  rows={4}
                  placeholder="Option 1&#10;Option 2&#10;Option 3"
                  value={field.options ? field.options.join('\n') : ''}
                  onChange={(e) => handleOptionsChange(e.target.value)}
                />
              </div>
            )}

            <div className="form-group">
              <label>Help Text</label>
              <input
                type="text"
                className="form-input"
                placeholder="Optional help text for users"
                value={field.helpText || ''}
                onChange={(e) => updateField(sectionId, field.id, { helpText: e.target.value })}
              />
            </div>

            <div className="form-group">
              <label className="checkbox-label">
                <input
                  type="checkbox"
                  checked={field.required}
                  onChange={(e) => updateField(sectionId, field.id, { required: e.target.checked })}
                  disabled={isLeadQuestionnaire}
                />
                <span>Required field</span>
                {isLeadQuestionnaire && (
                  <span className="help-text" style={{ marginLeft: '8px', color: '#6b7280' }}>
                    (Lead questionnaire cannot have required fields)
                  </span>
                )}
              </label>
            </div>
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="forms-builder">
      <div className="builder-header">
        <div style={{ display: 'flex', alignItems: 'center', gap: '16px' }}>
          {onBack && (
            <button className="btn btn-secondary" onClick={onBack}>
              <ArrowLeft size={18} />
              Back to Settings
            </button>
          )}
          <div>
            <h1 className="builder-title">{form.name}</h1>
            <p className="builder-subtitle">{form.description}</p>
            {isLeadQuestionnaire && (
              <div className="builder-notice">
                <AlertCircle size={16} />
                <span>Note: Lead questionnaire fields cannot be marked as required</span>
              </div>
            )}
          </div>
        </div>
        <div className="builder-actions">
          <button
            className="btn btn-secondary"
            onClick={() => setPreviewMode(!previewMode)}
          >
            <Eye size={18} />
            {previewMode ? 'Edit' : 'Preview'}
          </button>
          <button 
            className={`btn btn-primary ${hasChanges ? 'has-changes' : ''}`}
            onClick={saveForm}
          >
            <Save size={18} />
            {hasChanges ? 'Save Changes' : 'Save Form'}
          </button>
        </div>
      </div>

      {!previewMode ? (
        <div className="builder-content">
          {/* Sections Sidebar */}
          <div className="builder-sidebar">
            <div className="sidebar-header">
              <h3>Sections</h3>
              <button className="btn-icon" onClick={addSection} title="Add section">
                <Plus size={16} />
              </button>
            </div>
            <div className="sections-list">
              {form.sections.map(section => (
                <div
                  key={section.id}
                  className={`section-item ${activeSection === section.id ? 'active' : ''}`}
                >
                  <button
                    className="section-item-button"
                    onClick={() => setActiveSection(section.id)}
                  >
                    <span>{section.title}</span>
                    <span className="section-field-count">{section.fields.length}</span>
                  </button>
                  {form.sections.length > 1 && (
                    <button
                      className="btn-icon-sm"
                      onClick={() => deleteSection(section.id)}
                      title="Delete section"
                    >
                      <Trash2 size={14} />
                    </button>
                  )}
                </div>
              ))}
            </div>
          </div>

          {/* Field Editor */}
          <div className="builder-main">
            {currentSection && (
              <>
                <div className="section-editor-header">
                  <input
                    type="text"
                    className="section-title-input"
                    value={currentSection.title}
                    placeholder="Section Title"
                    onChange={(e) => updateSectionTitle(activeSection, e.target.value)}
                  />
                  <button className="btn btn-primary btn-sm" onClick={() => addField('text')}>
                    <Plus size={16} />
                    Add Field
                  </button>
                </div>

                <div className="fields-list">
                  {currentSection.fields.length === 0 ? (
                    <div className="empty-state">
                      <FileText size={48} style={{ opacity: 0.2 }} />
                      <p>No fields yet</p>
                      <button className="btn btn-primary" onClick={() => addField('text')}>
                        <Plus size={18} />
                        Add First Field
                      </button>
                    </div>
                  ) : (
                    currentSection.fields.map(field => (
                      <FieldEditor
                        key={field.id}
                        field={field}
                        sectionId={currentSection.id}
                      />
                    ))
                  )}
                </div>
              </>
            )}
          </div>

          {/* Field Types Palette */}
          <div className="builder-palette">
            <h3>Field Types</h3>
            <p className="palette-subtitle">Click to add field</p>
            <div className="field-types-list">
              {fieldTypes.map(type => {
                const Icon = type.icon;
                return (
                  <button
                    key={type.value}
                    className="field-type-button"
                    onClick={() => addField(type.value)}
                    title={`Add ${type.label} field`}
                  >
                    <Icon size={20} />
                    <span>{type.label}</span>
                  </button>
                );
              })}
            </div>
          </div>
        </div>
      ) : (
        <div className="builder-preview">
          <div className="preview-container">
            <div className="preview-header">
              <h2>Form Preview</h2>
              <p className="preview-subtitle">This is how the form will appear to users</p>
            </div>
            {form.sections.map(section => (
              <div key={section.id} className="preview-section">
                <h3 className="preview-section-title">{section.title}</h3>
                {section.repeatable && (
                  <span className="preview-repeatable-badge">Repeatable Section</span>
                )}
                <div className="preview-fields">
                  {section.fields.map(field => (
                    <div key={field.id} className="preview-field">
                      <label className="preview-label">
                        {field.label}
                        {field.required && <span className="required">*</span>}
                      </label>
                      {field.helpText && (
                        <p className="preview-help-text">{field.helpText}</p>
                      )}
                      {field.type === 'textarea' ? (
                        <textarea className="form-input" rows={3} disabled />
                      ) : field.type === 'select' ? (
                        <select className="form-select" disabled>
                          <option>Select...</option>
                          {field.options && field.options.map((opt, idx) => (
                            <option key={idx}>{opt}</option>
                          ))}
                        </select>
                      ) : field.type === 'checkbox' ? (
                        <div className="preview-checkbox-group">
                          {field.options && field.options.length > 0 ? (
                            field.options.map((opt, idx) => (
                              <label key={idx} className="checkbox-label">
                                <input type="checkbox" disabled />
                                <span>{opt}</span>
                              </label>
                            ))
                          ) : (
                            <label className="checkbox-label">
                              <input type="checkbox" disabled />
                              <span>{field.label}</span>
                            </label>
                          )}
                        </div>
                      ) : (
                        <input type={field.type} className="form-input" disabled />
                      )}
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default FormsBuilder;
