import React, { useState } from 'react';
import { User, Users, Workflow, Bell, Database, Palette, Shield, Key, Settings, Save, Plus, Trash2, X, Eye, ToggleLeft, ToggleRight, CheckCircle, ChevronRight } from 'lucide-react';

const SettingsTab = () => {
  const [activeSection, setActiveSection] = useState('general');

  // Owner Application Settings State
  const [showApplicationsView, setShowApplicationsView] = useState(false);
  const [showQuestionModal, setShowQuestionModal] = useState(false);
  const [selectedApplication, setSelectedApplication] = useState(null);
  const [showApplicationEditor, setShowApplicationEditor] = useState(false);
  const [editingApplication, setEditingApplication] = useState(null);
  const [applications, setApplications] = useState([
    {
      id: 'app1',
      name: 'Property Owner Application',
      description: 'Standard application for property owners seeking management services',
      isActive: true,
      createdAt: '2024-01-10T10:00:00Z',
      updatedAt: '2024-01-14T10:30:00Z',
      customQuestions: [
        {
          id: 'q1',
          label: 'How many properties do you currently own?',
          type: 'number',
          weight: 0.3,
          options: [],
          order: 1,
          required: false,
          active: true
        },
        {
          id: 'q2',
          label: 'Are you interested in full-service property management?',
          type: 'boolean',
          weight: 0.4,
          options: [],
          order: 2,
          required: true,
          active: true
        },
        {
          id: 'q3',
          label: 'What type of properties do you own?',
          type: 'select',
          weight: 0.2,
          options: ['Single Family', 'Multi-Family', 'Commercial', 'Mixed'],
          order: 3,
          required: false,
          active: true
        },
        {
          id: 'q4',
          label: 'Tell us about your property management experience',
          type: 'textarea',
          weight: 0.1,
          options: [],
          order: 4,
          required: false,
          active: true
        }
      ],
      eliminatingRules: [
        {
          id: 'r1',
          label: 'More than 30 minutes from office disqualifies',
          field: 'distanceMinutesFromOffice',
          op: '>',
          value: 30,
          isDisqualifying: true
        }
      ],
      applicationSettings: {
        autoEvaluate: true,
        sendNotifications: true,
        requirePhoneVerification: false,
        maxApplicationsPerDay: 50,
        applicationTimeout: 30
      }
    },
    {
      id: 'app2',
      name: 'Commercial Property Application',
      description: 'Specialized application for commercial property owners',
      isActive: false,
      createdAt: '2024-01-12T14:00:00Z',
      updatedAt: '2024-01-12T14:00:00Z',
      customQuestions: [
        {
          id: 'q1',
          label: 'What type of commercial properties do you own?',
          type: 'select',
          weight: 0.4,
          options: ['Office Buildings', 'Retail Spaces', 'Industrial', 'Mixed Use'],
          order: 1,
          required: true,
          active: true
        },
        {
          id: 'q2',
          label: 'Total square footage of properties',
          type: 'number',
          weight: 0.3,
          options: [],
          order: 2,
          required: false,
          active: true
        }
      ],
      eliminatingRules: [],
      applicationSettings: {
        autoEvaluate: true,
        sendNotifications: true,
        requirePhoneVerification: true,
        maxApplicationsPerDay: 25,
        applicationTimeout: 45
      }
    }
  ]);

  // Mock submitted applications data (responses to the application forms)
  const [submittedApplications, setSubmittedApplications] = useState([
    {
      id: 'sub1',
      applicationId: 'app1',
      ownerName: 'Sarah Johnson',
      email: 'sarah.johnson@email.com',
      phone: '(555) 123-4567',
      status: 'completed',
      submittedAt: '2024-01-14T10:30:00Z',
      lastActivityAt: '2024-01-14T10:30:00Z',
      outreachActive: true,
      distanceMinutesFromOffice: 15,
      leadSource: 'Google Ads',
      answers: {
        q1: 3,
        q2: true,
        q3: 'Single Family',
        q4: 'I have been managing my own properties for 5 years'
      },
      matchBand: 'great',
      disqualifiedByRuleId: null
    },
    {
      id: 'sub2',
      applicationId: 'app1',
      ownerName: 'Michael Chen',
      email: 'michael.chen@email.com',
      phone: '(555) 234-5678',
      status: 'completed',
      submittedAt: '2024-01-13T14:20:00Z',
      lastActivityAt: '2024-01-13T14:20:00Z',
      outreachActive: true,
      distanceMinutesFromOffice: 45,
      leadSource: 'Facebook',
      answers: {
        q1: 1,
        q2: false,
        q3: 'Multi-Family',
        q4: 'New to property management'
      },
      matchBand: 'bad',
      disqualifiedByRuleId: 'r1'
    },
    {
      id: 'sub3',
      applicationId: 'app1',
      ownerName: 'Emily Rodriguez',
      email: 'emily.rodriguez@email.com',
