                <div style={{ display: 'grid', gap: '1rem' }}>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
                    <label style={{ 
                      display: 'flex', 
                      alignItems: 'center', 
                      gap: '0.5rem',
                      cursor: 'pointer',
                      fontSize: '0.875rem'
                    }}>
                      <input
                        type="checkbox"
                        checked={editingApplication.applicationSettings.autoEvaluate}
                        onChange={(e) => setEditingApplication({
                          ...editingApplication,
                          applicationSettings: {
                            ...editingApplication.applicationSettings,
                            autoEvaluate: e.target.checked
                          }
                        })}
                        style={{ margin: 0 }}
                      />
                      Auto-evaluate applications
                    </label>
                  </div>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
                    <label style={{ 
                      display: 'flex', 
                      alignItems: 'center', 
                      gap: '0.5rem',
                      cursor: 'pointer',
                      fontSize: '0.875rem'
                    }}>
                      <input
                        type="checkbox"
                        checked={editingApplication.applicationSettings.sendNotifications}
                        onChange={(e) => setEditingApplication({
                          ...editingApplication,
                          applicationSettings: {
                            ...editingApplication.applicationSettings,
                            sendNotifications: e.target.checked
                          }
                        })}
                        style={{ margin: 0 }}
                      />
                      Send email notifications
                    </label>
                  </div>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
                    <label style={{ 
                      display: 'flex', 
                      alignItems: 'center', 
                      gap: '0.5rem',
                      cursor: 'pointer',
                      fontSize: '0.875rem'
                    }}>
                      <input
                        type="checkbox"
                        checked={editingApplication.applicationSettings.requirePhoneVerification}
                        onChange={(e) => setEditingApplication({
                          ...editingApplication,
                          applicationSettings: {
                            ...editingApplication.applicationSettings,
                            requirePhoneVerification: e.target.checked
                          }
                        })}
                        style={{ margin: 0 }}
                      />
                      Require phone verification
                    </label>
                  </div>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
                    <label style={{ fontSize: '0.875rem', minWidth: '200px' }}>
                      Max applications per day:
                    </label>
                    <input
                      type="number"
                      value={editingApplication.applicationSettings.maxApplicationsPerDay}
                      onChange={(e) => setEditingApplication({
                        ...editingApplication,
                        applicationSettings: {
                          ...editingApplication.applicationSettings,
                          maxApplicationsPerDay: parseInt(e.target.value) || 0
                        }
                      })}
                      style={{
                        padding: '0.5rem',
                        border: '1px solid #d1d5db',
                        borderRadius: '4px',
                        width: '100px'
                      }}
                    />
                  </div>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
                    <label style={{ fontSize: '0.875rem', minWidth: '200px' }}>
                      Application timeout (minutes):
                    </label>
                    <input
                      type="number"
                      value={editingApplication.applicationSettings.applicationTimeout}
                      onChange={(e) => setEditingApplication({
                        ...editingApplication,
                        applicationSettings: {
                          ...editingApplication.applicationSettings,
                          applicationTimeout: parseInt(e.target.value) || 0
                        }
                      })}
                      style={{
                        padding: '0.5rem',
                        border: '1px solid #d1d5db',
                        borderRadius: '4px',
                        width: '100px'
                      }}
                    />
                  </div>
                </div>
              </div>

              {/* Custom Questions */}
              <div style={{ marginBottom: '2rem' }}>
                <div style={{ 
                  display: 'flex', 
                  justifyContent: 'space-between', 
                  alignItems: 'center', 
                  marginBottom: '1rem' 
                }}>
                  <h3 style={{ 
                    fontSize: '1.125rem', 
                    fontWeight: '600', 
                    margin: 0, 
                    color: '#1e293b' 
                  }}>
                    Custom Questions ({editingApplication.customQuestions.length})
                  </h3>
                  <button
                    onClick={addCustomQuestion}
                    style={{
                      display: 'flex',
                      alignItems: 'center',
                      gap: '0.5rem',
                      padding: '0.5rem 1rem',
                      backgroundColor: '#10b981',
                      color: 'white',
                      border: 'none',
                      borderRadius: '6px',
                      cursor: 'pointer',
                      fontSize: '0.875rem',
                      fontWeight: '500'
                    }}
                  >
                    <Plus size={16} />
                    Add Question
                  </button>
                </div>
                <div style={{ display: 'grid', gap: '1rem' }}>
                  {editingApplication.customQuestions
                    .sort((a, b) => a.order - b.order)
                    .map((question, index) => (
                    <div key={question.id} style={{
                      padding: '1rem',
                      border: '1px solid #e2e8f0',
                      borderRadius: '6px',
                      backgroundColor: question.active ? '#f8fafc' : '#f3f4f6',
                      opacity: question.active ? 1 : 0.7
                    }}>
                      <div style={{ display: 'grid', gap: '0.75rem' }}>
                        <div style={{ display: 'flex', gap: '1rem', alignItems: 'center' }}>
                          {/* Order Controls */}
                          <div style={{ display: 'flex', flexDirection: 'column', gap: '0.25rem' }}>
                            <button
                              onClick={() => moveQuestionUp(question.id)}
                              disabled={index === 0}
                              style={{
                                background: 'none',
                                border: 'none',
                                cursor: index === 0 ? 'not-allowed' : 'pointer',
                                padding: '0.25rem',
                                borderRadius: '4px',
                                color: index === 0 ? '#9ca3af' : '#64748b',
                                opacity: index === 0 ? 0.5 : 1
                              }}
                              title="Move up"
                            >
                              <ChevronRight size={16} style={{ transform: 'rotate(-90deg)' }} />
                            </button>
                            <button
                              onClick={() => moveQuestionDown(question.id)}
                              disabled={index === editingApplication.customQuestions.length - 1}
                              style={{
                                background: 'none',
                                border: 'none',
                                cursor: index === editingApplication.customQuestions.length - 1 ? 'not-allowed' : 'pointer',
                                padding: '0.25rem',
                                borderRadius: '4px',
                                color: index === editingApplication.customQuestions.length - 1 ? '#9ca3af' : '#64748b',
                                opacity: index === editingApplication.customQuestions.length - 1 ? 0.5 : 1
                              }}
                              title="Move down"
                            >
                              <ChevronRight size={16} style={{ transform: 'rotate(90deg)' }} />
                            </button>
                          </div>

                          {/* Question Content */}
                          <div style={{ flex: 1, display: 'flex', gap: '1rem', alignItems: 'center' }}>
                            <input
                              type="text"
                              value={question.label}
                              onChange={(e) => updateCustomQuestion(question.id, { label: e.target.value })}
                              placeholder="Question label"
                              style={{
                                flex: 1,
                                padding: '0.5rem',
                                border: '1px solid #d1d5db',
                                borderRadius: '4px',
                                fontSize: '0.875rem'
                              }}
                            />
                            <select
                              value={question.type}
                              onChange={(e) => updateCustomQuestion(question.id, { type: e.target.value })}
                              style={{
                                padding: '0.5rem',
                                border: '1px solid #d1d5db',
                                borderRadius: '4px',
                                fontSize: '0.875rem',
                                minWidth: '120px'
                              }}
                            >
                              <option value="text">Text Field</option>
                              <option value="textarea">Text Area</option>
                              <option value="number">Number</option>
                              <option value="boolean">Yes/No</option>
                              <option value="select">Select</option>
                            </select>
                            <input
                              type="number"
                              min="0"
                              max="1"
                              step="0.1"
                              value={question.weight}
                              onChange={(e) => updateCustomQuestion(question.id, { weight: parseFloat(e.target.value) })}
                              placeholder="Weight"
                              style={{
                                padding: '0.5rem',
                                border: '1px solid #d1d5db',
                                borderRadius: '4px',
                                fontSize: '0.875rem',
                                width: '80px'
                              }}
                            />
                          </div>

                          {/* Toggle Controls */}
                          <div style={{ display: 'flex', gap: '0.5rem', alignItems: 'center' }}>
                            <button
                              onClick={() => toggleQuestionRequired(question.id)}
                              style={{
                                background: 'none',
                                border: 'none',
                                cursor: 'pointer',
                                padding: '0.25rem',
                                borderRadius: '4px',
                                color: question.required ? '#dc2626' : '#9ca3af'
                              }}
                              title={question.required ? 'Required' : 'Optional'}
                            >
                              {question.required ? <ToggleRight size={20} /> : <ToggleLeft size={20} />}
                            </button>
                            <button
                              onClick={() => toggleQuestionActive(question.id)}
                              style={{
                                background: 'none',
                                border: 'none',
                                cursor: 'pointer',
                                padding: '0.25rem',
                                borderRadius: '4px',
                                color: question.active ? '#10b981' : '#9ca3af'
                              }}
                              title={question.active ? 'Active' : 'Inactive'}
                            >
                              {question.active ? <CheckCircle size={20} /> : <X size={20} />}
                            </button>
                            <button
                              onClick={() => deleteCustomQuestion(question.id)}
                              style={{
                                background: 'none',
                                border: 'none',
                                cursor: 'pointer',
                                padding: '0.25rem',
                                borderRadius: '4px',
                                color: '#dc2626'
                              }}
                              title="Delete Question"
                            >
                              <Trash2 size={16} />
                            </button>
                          </div>
                        </div>
                        {question.type === 'select' && (
                          <div>
                            <label style={{ fontSize: '0.875rem', color: '#374151', marginBottom: '0.5rem', display: 'block' }}>
                              Options (comma-separated):
                            </label>
                            <input
                              type="text"
                              value={question.options.join(', ')}
                              onChange={(e) => updateCustomQuestion(question.id, { 
                                options: e.target.value.split(',').map(opt => opt.trim()).filter(opt => opt)
                              })}
                              placeholder="Option 1, Option 2, Option 3"
                              style={{
                                width: '100%',
                                padding: '0.5rem',
                                border: '1px solid #d1d5db',
                                borderRadius: '4px',
                                fontSize: '0.875rem'
                              }}
                            />
                          </div>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              {/* Eliminating Rules */}
              <div style={{ marginBottom: '2rem' }}>
                <div style={{ 
                  display: 'flex', 
                  justifyContent: 'space-between', 
                  alignItems: 'center', 
                  marginBottom: '1rem' 
                }}>
                  <h3 style={{ 
                    fontSize: '1.125rem', 
                    fontWeight: '600', 
                    margin: 0, 
                    color: '#1e293b' 
                  }}>
                    Eliminating Rules ({editingApplication.eliminatingRules.length})
                  </h3>
                  <button
                    onClick={addEliminatingRule}
                    style={{
                      display: 'flex',
                      alignItems: 'center',
                      gap: '0.5rem',
                      padding: '0.5rem 1rem',
                      backgroundColor: '#ef4444',
                      color: 'white',
                      border: 'none',
                      borderRadius: '6px',
                      cursor: 'pointer',
                      fontSize: '0.875rem',
                      fontWeight: '500'
                    }}
                  >
                    <Plus size={16} />
                    Add Rule
                  </button>
                </div>
                <div style={{ display: 'grid', gap: '1rem' }}>
                  {editingApplication.eliminatingRules.map((rule, index) => (
                    <div key={rule.id} style={{
                      padding: '1rem',
                      border: '1px solid #e2e8f0',
                      borderRadius: '6px',
                      backgroundColor: '#fef2f2'
                    }}>
                      <div style={{ display: 'grid', gap: '0.75rem' }}>
                        <div style={{ display: 'flex', gap: '1rem', alignItems: 'center' }}>
                          <input
                            type="text"
                            value={rule.label}
                            onChange={(e) => updateEliminatingRule(rule.id, { label: e.target.value })}
                            placeholder="Rule description"
                            style={{
                              flex: 1,
                              padding: '0.5rem',
                              border: '1px solid #d1d5db',
                              borderRadius: '4px',
                              fontSize: '0.875rem'
                            }}
                          />
                          <select
                            value={rule.field}
                            onChange={(e) => updateEliminatingRule(rule.id, { field: e.target.value })}
                            style={{
                              padding: '0.5rem',
                              border: '1px solid #d1d5db',
                              borderRadius: '4px',
                              fontSize: '0.875rem',
                              minWidth: '150px'
                            }}
                          >
                            <option value="">Select Field</option>
                            <option value="distanceMinutesFromOffice">Distance from Office</option>
                            <option value="creditScore">Credit Score</option>
                            <option value="income">Income</option>
                            <option value="propertyCount">Property Count</option>
                          </select>
                          <select
                            value={rule.op}
                            onChange={(e) => updateEliminatingRule(rule.id, { op: e.target.value })}
                            style={{
                              padding: '0.5rem',
                              border: '1px solid #d1d5db',
                              borderRadius: '4px',
                              fontSize: '0.875rem',
                              minWidth: '80px'
                            }}
                          >
                            <option value=">">Greater than</option>
                            <option value="<">Less than</option>
                            <option value=">=">Greater or equal</option>
                            <option value="<=">Less or equal</option>
                            <option value="==">Equal to</option>
                            <option value="!=">Not equal</option>
                          </select>
                          <input
                            type="text"
                            value={rule.value}
                            onChange={(e) => updateEliminatingRule(rule.id, { value: e.target.value })}
                            placeholder="Value"
                            style={{
                              padding: '0.5rem',
                              border: '1px solid #d1d5db',
                              borderRadius: '4px',
                              fontSize: '0.875rem',
                              width: '100px'
                            }}
                          />
                          <button
                            onClick={() => deleteEliminatingRule(rule.id)}
                            style={{
                              background: 'none',
                              border: 'none',
                              cursor: 'pointer',
                              padding: '0.5rem',
                              borderRadius: '4px',
                              color: '#dc2626'
                            }}
                            title="Delete Rule"
                          >
                            <Trash2 size={16} />
                          </button>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>

            {/* Modal Footer */}
            <div style={{
              padding: '1rem 2rem 2rem 2rem',
              borderTop: '1px solid #e2e8f0',
              display: 'flex',
              justifyContent: 'flex-end',
              gap: '1rem'
            }}>
              <button
                onClick={() => {
                  setShowApplicationEditor(false);
                  setEditingApplication(null);
                }}
                style={{
                  padding: '0.75rem 1.5rem',
                  backgroundColor: '#f8fafc',
                  color: '#374151',
                  border: '1px solid #e2e8f0',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  fontSize: '0.875rem',
                  fontWeight: '500'
                }}
              >
                Cancel
              </button>
              <button
                onClick={saveApplication}
                disabled={!editingApplication.name.trim()}
                style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '0.5rem',
                  padding: '0.75rem 1.5rem',
                  backgroundColor: editingApplication.name.trim() ? '#3b82f6' : '#9ca3af',
                  color: 'white',
                  border: 'none',
                  borderRadius: '6px',
                  cursor: editingApplication.name.trim() ? 'pointer' : 'not-allowed',
                  fontSize: '0.875rem',
                  fontWeight: '500'
                }}
              >
                <Save size={16} />
                {editingApplication.id.startsWith('app') && editingApplication.id.length > 3 ? 'Update Application' : 'Create Application'}
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Question Management Modal */}
      {showQuestionModal && selectedApplication && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backgroundColor: 'rgba(0, 0, 0, 0.5)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          zIndex: 1000,
          padding: '1rem'
        }}>
          <div style={{
            backgroundColor: 'white',
            borderRadius: '12px',
            width: '100%',
            maxWidth: '900px',
            maxHeight: '90vh',
            overflow: 'auto',
            boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.25)',
            display: 'flex',
            flexDirection: 'column'
          }}>
            {/* Modal Header */}
            <div style={{
              padding: '2rem 2rem 1rem 2rem',
              borderBottom: '1px solid #e2e8f0',
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'flex-start'
            }}>
              <div>
                <h2 style={{ 
                  margin: 0, 
                  color: '#1e293b', 
                  fontSize: '1.5rem',
                  fontWeight: '700',
                  marginBottom: '0.5rem'
                }}>
                  {selectedApplication.name} - Questions
                </h2>
                <p style={{ 
                  margin: 0, 
                  color: '#64748b',
                  fontSize: '0.875rem'
                }}>
                  View application questions and settings
                </p>
              </div>
              <button
                onClick={() => setShowQuestionModal(false)}
                style={{
                  background: 'none',
                  border: 'none',
                  cursor: 'pointer',
                  padding: '0.5rem',
                  borderRadius: '8px',
                  color: '#64748b',
                  transition: 'all 0.2s ease'
                }}
                onMouseEnter={(e) => e.currentTarget.style.backgroundColor = '#f1f5f9'}
                onMouseLeave={(e) => e.currentTarget.style.backgroundColor = 'transparent'}
              >
                <X size={24} />
              </button>
            </div>

            {/* Modal Content */}
            <div style={{ flex: 1, padding: '2rem' }}>
              {/* Application Info */}
              <div style={{ marginBottom: '2rem', padding: '1rem', backgroundColor: '#f8fafc', borderRadius: '8px' }}>
                <h3 style={{ margin: '0 0 0.5rem 0', color: '#1e293b', fontSize: '1rem', fontWeight: '600' }}>
                  Application Details
                </h3>
                <p style={{ margin: '0 0 0.5rem 0', color: '#64748b', fontSize: '0.875rem' }}>
                  <strong>Name:</strong> {selectedApplication.name}
                </p>
                <p style={{ margin: '0 0 0.5rem 0', color: '#64748b', fontSize: '0.875rem' }}>
                  <strong>Description:</strong> {selectedApplication.description || 'No description'}
                </p>
                <p style={{ margin: '0 0 0.5rem 0', color: '#64748b', fontSize: '0.875rem' }}>
                  <strong>Status:</strong> {selectedApplication.isActive ? 'Active' : 'Inactive'}
                </p>
                <p style={{ margin: '0 0 0.5rem 0', color: '#64748b', fontSize: '0.875rem' }}>
                  <strong>Questions:</strong> {selectedApplication.customQuestions.length}
                </p>
                <p style={{ margin: '0', color: '#64748b', fontSize: '0.875rem' }}>
                  <strong>Last Updated:</strong> {formatRelativeTime(selectedApplication.updatedAt)}
                </p>
              </div>

              {/* Custom Questions */}
              <div style={{ marginBottom: '2rem' }}>
                <h3 style={{ 
                  fontSize: '1.125rem', 
                  fontWeight: '600', 
                  margin: '0 0 1rem 0', 
                  color: '#1e293b' 
                }}>
                  Custom Questions ({selectedApplication.customQuestions.length})
                </h3>
                <div style={{ display: 'grid', gap: '1rem' }}>
                  {selectedApplication.customQuestions
                    .sort((a, b) => a.order - b.order)
                    .map((question, index) => (
                    <div key={question.id} style={{
                      padding: '1rem',
                      border: '1px solid #e2e8f0',
                      borderRadius: '6px',
                      backgroundColor: question.active ? '#f8fafc' : '#f3f4f6',
                      opacity: question.active ? 1 : 0.7
                    }}>
                      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: '0.5rem' }}>
                        <div style={{ flex: 1 }}>
                          <h4 style={{ margin: '0 0 0.25rem 0', color: '#1e293b', fontSize: '0.875rem', fontWeight: '600' }}>
                            {question.label || 'Untitled Question'}
                          </h4>
                          <div style={{ display: 'flex', gap: '1rem', alignItems: 'center', fontSize: '0.75rem', color: '#64748b' }}>
                            <span>Type: {question.type}</span>
                            <span>Weight: {question.weight}</span>
                            <span>Required: {question.required ? 'Yes' : 'No'}</span>
                            <span>Active: {question.active ? 'Yes' : 'No'}</span>
                          </div>
                        </div>
                        <div style={{ display: 'flex', gap: '0.5rem', alignItems: 'center' }}>
                          {question.required && (
                            <span style={{
                              padding: '0.25rem 0.5rem',
                              backgroundColor: '#dc2626',
                              color: 'white',
                              borderRadius: '4px',
                              fontSize: '0.75rem',
                              fontWeight: '500'
                            }}>
                              Required
                            </span>
                          )}
                          {question.active ? (
                            <CheckCircle size={16} color="#10b981" />
                          ) : (
                            <X size={16} color="#9ca3af" />
                          )}
                        </div>
                      </div>
                      {question.type === 'select' && question.options.length > 0 && (
                        <div style={{ marginTop: '0.5rem' }}>
                          <p style={{ margin: '0 0 0.25rem 0', fontSize: '0.75rem', color: '#64748b', fontWeight: '500' }}>
                            Options:
                          </p>
                          <div style={{ display: 'flex', flexWrap: 'wrap', gap: '0.25rem' }}>
                            {question.options.map((option, optIndex) => (
                              <span key={optIndex} style={{
                                padding: '0.125rem 0.5rem',
                                backgroundColor: '#e2e8f0',
                                color: '#374151',
                                borderRadius: '4px',
                                fontSize: '0.75rem'
                              }}>
                                {option}
                              </span>
                            ))}
                          </div>
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              </div>

              {/* Eliminating Rules */}
              <div style={{ marginBottom: '2rem' }}>
                <h3 style={{ 
                  fontSize: '1.125rem', 
                  fontWeight: '600', 
                  margin: '0 0 1rem 0', 
                  color: '#1e293b' 
                }}>
                  Eliminating Rules ({selectedApplication.eliminatingRules.length})
                </h3>
                <div style={{ display: 'grid', gap: '1rem' }}>
                  {selectedApplication.eliminatingRules.map((rule, index) => (
                    <div key={rule.id} style={{
                      padding: '1rem',
                      border: '1px solid #e2e8f0',
                      borderRadius: '6px',
                      backgroundColor: '#fef2f2'
                    }}>
                      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
                        <div style={{ flex: 1 }}>
                          <h4 style={{ margin: '0 0 0.25rem 0', color: '#1e293b', fontSize: '0.875rem', fontWeight: '600' }}>
                            {rule.label || 'Untitled Rule'}
                          </h4>
                          <div style={{ fontSize: '0.75rem', color: '#64748b' }}>
                            <span style={{ fontWeight: '500' }}>{rule.field}</span> {rule.op} <span style={{ fontWeight: '500' }}>{rule.value}</span>
                          </div>
                        </div>
                        <div style={{ color: '#dc2626' }}>
                          <X size={16} />
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
                // Application Details View
                <div>
                  <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '2rem', marginBottom: '2rem' }}>
                    {/* Owner Information */}
                    <div style={{
                      backgroundColor: '#f8fafc',
                      borderRadius: '8px',
                      padding: '1.5rem'
                    }}>
                      <h3 style={{ margin: '0 0 1rem 0', color: '#1e293b', fontSize: '1.125rem', fontWeight: '600' }}>
                        Owner Information
                      </h3>
                      <div style={{ display: 'flex', flexDirection: 'column', gap: '0.75rem' }}>
                        <div>
                          <label style={{ fontSize: '0.75rem', fontWeight: '500', color: '#64748b', textTransform: 'uppercase', letterSpacing: '0.05em' }}>
                            Name
                          </label>
                          <div style={{ fontSize: '0.875rem', color: '#1f2937', fontWeight: '500' }}>
                            {selectedApplication.ownerName}
                          </div>
                        </div>
                        <div>
                          <label style={{ fontSize: '0.75rem', fontWeight: '500', color: '#64748b', textTransform: 'uppercase', letterSpacing: '0.05em' }}>
                            Email
                          </label>
                          <div style={{ fontSize: '0.875rem', color: '#1f2937' }}>
                            {selectedApplication.email}
                          </div>
                        </div>
                        {selectedApplication.phone && (
                          <div>
                            <label style={{ fontSize: '0.75rem', fontWeight: '500', color: '#64748b', textTransform: 'uppercase', letterSpacing: '0.05em' }}>
                              Phone
                            </label>
                            <div style={{ fontSize: '0.875rem', color: '#1f2937' }}>
                              {selectedApplication.phone}
                            </div>
                          </div>
                        )}
                        <div>
                          <label style={{ fontSize: '0.75rem', fontWeight: '500', color: '#64748b', textTransform: 'uppercase', letterSpacing: '0.05em' }}>
                            Lead Source
                          </label>
                          <div style={{ fontSize: '0.875rem', color: '#1f2937' }}>
                            {selectedApplication.leadSource}
                          </div>
                        </div>
                      </div>
                    </div>

                    {/* Match Assessment */}
                    {selectedApplication.status === 'completed' && (
                      <div style={{
                        backgroundColor: '#f8fafc',
                        borderRadius: '8px',
                        padding: '1.5rem'
                      }}>
                        <h3 style={{ margin: '0 0 1rem 0', color: '#1e293b', fontSize: '1.125rem', fontWeight: '600' }}>
                          Match Assessment
                        </h3>
                        <div style={{ marginBottom: '1rem' }}>
                          <span style={{
                            display: 'inline-block',
                            padding: '0.75rem 1.5rem',
                            borderRadius: '12px',
                            fontSize: '1rem',
                            fontWeight: '600',
                            border: '2px solid',
                            ...getBandColor(selectedApplication.matchBand)
                          }}>
                            {getBandLabel(selectedApplication.matchBand)}
                          </span>
                        </div>
                        {selectedApplication.disqualifiedByRuleId && (
                          <div style={{ 
                            padding: '1rem',
                            backgroundColor: '#fef2f2',
                            border: '1px solid #fecaca',
                            borderRadius: '8px',
                            fontSize: '0.875rem',
                            color: '#dc2626'
                          }}>
                            <strong>Disqualified by:</strong> {ownerSettings.eliminatingRules.find(r => r.id === selectedApplication.disqualifiedByRuleId)?.label}
                          </div>
                        )}
                      </div>
                    )}
                  </div>

                  {/* Application Answers */}
                  <div style={{ marginBottom: '2rem' }}>
                    <h3 style={{ margin: '0 0 1rem 0', color: '#1e293b', fontSize: '1.125rem', fontWeight: '600' }}>
                      Application Answers
                    </h3>
                    <div style={{ display: 'grid', gap: '1rem' }}>
                      {ownerSettings.customQuestions
                        .filter(q => q.active)
                        .sort((a, b) => a.order - b.order)
                        .map(question => (
                        <div key={question.id} style={{
                          backgroundColor: 'white',
                          border: '1px solid #e2e8f0',
                          borderRadius: '8px',
                          padding: '1.5rem'
                        }}>
                          <div style={{ fontWeight: '600', marginBottom: '0.5rem', color: '#374151' }}>
                            {question.label}
                            {question.required && (
                              <span style={{ color: '#dc2626', marginLeft: '0.5rem' }}>*</span>
                            )}
                          </div>
                          <div style={{ 
                            fontSize: '0.875rem', 
                            color: '#6b7280',
                            padding: '0.75rem',
                            backgroundColor: '#f9fafb',
                            borderRadius: '6px',
                            border: '1px solid #f3f4f6'
                          }}>
                            {selectedApplication.answers[question.id] !== undefined 
                              ? selectedApplication.answers[question.id].toString()
                              : 'Not answered'
                            }
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              ) : (
                // Question Management View
                <div>
                  <div style={{ 
                    display: 'flex', 
                    justifyContent: 'space-between', 
                    alignItems: 'center', 
                    marginBottom: '1.5rem' 
                  }}>
                    <h3 style={{ 
                      fontSize: '1.125rem', 
                      fontWeight: '600', 
                      margin: 0, 
                      color: '#374151' 
                    }}>
                      Questions ({ownerSettings.customQuestions.length})
                    </h3>
                    <button
                      onClick={addCustomQuestion}
                      style={{
                        display: 'flex',
                        alignItems: 'center',
                        gap: '0.5rem',
                        padding: '0.5rem 1rem',
                        backgroundColor: '#10b981',
                        color: 'white',
                        border: 'none',
                        borderRadius: '6px',
                        cursor: 'pointer',
                        fontSize: '0.875rem',
                        fontWeight: '500'
                      }}
                    >
                      <Plus size={16} />
                      Add Question
                    </button>
                  </div>

                  <div style={{ display: 'grid', gap: '1rem' }}>
                    {ownerSettings.customQuestions
                      .sort((a, b) => a.order - b.order)
                      .map((question, index) => (
                      <div key={question.id} style={{
                        padding: '1rem',
                        border: '1px solid #e2e8f0',
                        borderRadius: '6px',
                        backgroundColor: question.active ? '#f8fafc' : '#f3f4f6',
                        opacity: question.active ? 1 : 0.7
                      }}>
                        <div style={{ display: 'grid', gap: '0.75rem' }}>
                          <div style={{ display: 'flex', gap: '1rem', alignItems: 'center' }}>
                            {/* Order Controls */}
                            <div style={{ display: 'flex', flexDirection: 'column', gap: '0.25rem' }}>
                              <button
                                onClick={() => moveQuestionUp(question.id)}
                                disabled={index === 0}
                                style={{
                                  background: 'none',
                                  border: 'none',
                                  cursor: index === 0 ? 'not-allowed' : 'pointer',
                                  padding: '0.25rem',
                                  borderRadius: '4px',
                                  color: index === 0 ? '#9ca3af' : '#64748b',
                                  opacity: index === 0 ? 0.5 : 1
                                }}
                                title="Move up"
                              >
                                <ChevronRight size={16} style={{ transform: 'rotate(-90deg)' }} />
                              </button>
                              <button
                                onClick={() => moveQuestionDown(question.id)}
                                disabled={index === ownerSettings.customQuestions.length - 1}
                                style={{
                                  background: 'none',
                                  border: 'none',
                                  cursor: index === ownerSettings.customQuestions.length - 1 ? 'not-allowed' : 'pointer',
                                  padding: '0.25rem',
                                  borderRadius: '4px',
                                  color: index === ownerSettings.customQuestions.length - 1 ? '#9ca3af' : '#64748b',
                                  opacity: index === ownerSettings.customQuestions.length - 1 ? 0.5 : 1
                                }}
                                title="Move down"
                              >
                                <ChevronRight size={16} style={{ transform: 'rotate(90deg)' }} />
                              </button>
                            </div>

                            {/* Question Content */}
                            <div style={{ flex: 1, display: 'flex', gap: '1rem', alignItems: 'center' }}>
                              <input
                                type="text"
                                value={question.label}
                                onChange={(e) => updateCustomQuestion(question.id, { label: e.target.value })}
                                placeholder="Question label"
                                style={{
                                  flex: 1,
                                  padding: '0.5rem',
                                  border: '1px solid #d1d5db',
                                  borderRadius: '4px',
                                  fontSize: '0.875rem'
                                }}
                              />
                              <select
                                value={question.type}
                                onChange={(e) => updateCustomQuestion(question.id, { type: e.target.value })}
                                style={{
                                  padding: '0.5rem',
                                  border: '1px solid #d1d5db',
                                  borderRadius: '4px',
                                  fontSize: '0.875rem',
                                  minWidth: '120px'
                                }}
                              >
                                <option value="text">Text Field</option>
                                <option value="textarea">Text Area</option>
                                <option value="number">Number</option>
                                <option value="boolean">Yes/No</option>
                                <option value="select">Select</option>
                              </select>
                              <input
                                type="number"
                                min="0"
                                max="1"
                                step="0.1"
                                value={question.weight}
                                onChange={(e) => updateCustomQuestion(question.id, { weight: parseFloat(e.target.value) })}
                                placeholder="Weight"
                                style={{
                                  padding: '0.5rem',
                                  border: '1px solid #d1d5db',
                                  borderRadius: '4px',
                                  fontSize: '0.875rem',
                                  width: '80px'
                                }}
                              />
                            </div>

                            {/* Toggle Controls */}
                            <div style={{ display: 'flex', gap: '0.5rem', alignItems: 'center' }}>
                              <button
                                onClick={() => toggleQuestionRequired(question.id)}
                                style={{
                                  background: 'none',
                                  border: 'none',
                                  cursor: 'pointer',
                                  padding: '0.25rem',
                                  borderRadius: '4px',
                                  color: question.required ? '#dc2626' : '#9ca3af'
                                }}
                                title={question.required ? 'Required' : 'Optional'}
                              >
                                {question.required ? <ToggleRight size={20} /> : <ToggleLeft size={20} />}
                              </button>
                              <button
                                onClick={() => toggleQuestionActive(question.id)}
                                style={{
                                  background: 'none',
                                  border: 'none',
                                  cursor: 'pointer',
                                  padding: '0.25rem',
                                  borderRadius: '4px',
                                  color: question.active ? '#10b981' : '#9ca3af'
                                }}
                                title={question.active ? 'Active' : 'Inactive'}
                              >
                                {question.active ? <CheckCircle size={20} /> : <X size={20} />}
                              </button>
                              <button
                                onClick={() => deleteCustomQuestion(question.id)}
                                style={{
                                  background: 'none',
                                  border: 'none',
                                  cursor: 'pointer',
                                  padding: '0.25rem',
                                  borderRadius: '4px',
                                  color: '#dc2626'
                                }}
                                title="Delete Question"
                              >
                                <Trash2 size={16} />
                              </button>
                            </div>
                          </div>
                          {question.type === 'select' && (
                            <div>
                              <label style={{ fontSize: '0.875rem', color: '#374151', marginBottom: '0.5rem', display: 'block' }}>
                                Options (comma-separated):
                              </label>
                              <input
                                type="text"
                                value={question.options.join(', ')}
                                onChange={(e) => updateCustomQuestion(question.id, { 
                                  options: e.target.value.split(',').map(opt => opt.trim()).filter(opt => opt)
                                })}
                                placeholder="Option 1, Option 2, Option 3"
                                style={{
                                  width: '100%',
                                  padding: '0.5rem',
                                  border: '1px solid #d1d5db',
                                  borderRadius: '4px',
                                  fontSize: '0.875rem'
                                }}
                              />
                            </div>
                          )}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SettingsTab;
