import React, { useState, useRef, useEffect, useCallback, useMemo } from 'react';
import { 
  Play, 
  Pause, 
  Save, 
  Plus, 
  Trash2, 
  Edit, 
  Copy, 
  Settings,
  Mail,
  MessageSquare,
  Users,
  UserPlus,
  ArrowRight,
  Clock,
  CheckCircle,
  AlertCircle,
  Calendar,
  Phone,
  FileText,
  Database,
  Zap,
  GripVertical,
  X,
  RotateCcw
} from 'lucide-react';

const VisualWorkflowBuilder = ({ editingWorkflowId, selectedFolder, onClose, onSave }) => {
  const [workflows, setWorkflows] = useState([
    {
      id: 1,
      name: 'New Lead Welcome',
      status: 'active',
      description: 'Send welcome email when new lead is added',
      lastRun: '2024-01-14T10:30:00Z',
      runs: 24
    },
    {
      id: 2,
      name: 'Tour Reminder',
      status: 'active',
      description: 'Send text reminder 1 hour before scheduled tour',
      lastRun: '2024-01-14T14:15:00Z',
      runs: 8
    }
  ]);

  const [selectedWorkflow, setSelectedWorkflow] = useState(null);
  const [showBuilder, setShowBuilder] = useState(false);
  const [workflowName, setWorkflowName] = useState('');
  const [workflowDescription, setWorkflowDescription] = useState('');
  const [workflowFolder, setWorkflowFolder] = useState(selectedFolder || 'system');

  // Effect to handle editing existing workflows
  useEffect(() => {
    if (editingWorkflowId) {
      // In a real app, you would fetch the workflow data here
      // For now, we'll simulate loading the workflow
      const workflow = workflows.find(w => w.id === editingWorkflowId);
      if (workflow) {
        setWorkflowName(workflow.name);
        setWorkflowDescription(workflow.description);
        setWorkflowFolder(workflow.folderId || 'system');
        // Load existing nodes and connections if they exist
        if (workflow.nodes) {
          setNodes(workflow.nodes);
        }
        if (workflow.connections) {
          setConnections(workflow.connections);
        }
        setShowBuilder(true);
      }
    } else if (selectedFolder) {
      setWorkflowFolder(selectedFolder);
      setShowBuilder(true);
    }
  }, [editingWorkflowId, selectedFolder]);

  // Folder data structure
  const [folders, setFolders] = useState([
    {
      id: 'system',
      name: 'System',
      type: 'system',
      parentId: null,
      children: ['lead-management', 'customer-service', 'data-sync'],
      workflows: []
    },
    {
      id: 'lead-management',
      name: 'Lead Management',
      type: 'custom',
      parentId: 'system',
      children: ['lead-nurturing'],
      workflows: []
    },
    {
      id: 'lead-nurturing',
      name: 'Lead Nurturing',
      type: 'custom',
      parentId: 'lead-management',
      children: [],
      workflows: []
    },
    {
      id: 'customer-service',
      name: 'Customer Service',
      type: 'custom',
      parentId: 'system',
      children: [],
      workflows: []
    },
    {
      id: 'data-sync',
      name: 'Data Synchronization',
      type: 'custom',
      parentId: 'system',
      children: [],
      workflows: []
    }
  ]);

  // Canvas state
  const canvasRef = useRef(null);
  const [nodes, setNodes] = useState([]);
  const [connections, setConnections] = useState([]);
  const [draggedNode, setDraggedNode] = useState(null);
  const [isConnecting, setIsConnecting] = useState(false);
  const [connectionStart, setConnectionStart] = useState(null);
  const [canvasOffset, setCanvasOffset] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const [isNodeDragging, setIsNodeDragging] = useState(false);
  const [previewConnection, setPreviewConnection] = useState(null);
  const [hoveredConnection, setHoveredConnection] = useState(null);
  const [hoveredConnectionPoint, setHoveredConnectionPoint] = useState(null);
  const [configuringNode, setConfiguringNode] = useState(null);
  const [nodeConfig, setNodeConfig] = useState({});

  // Configuration data
  const [emailTemplates] = useState([
    { id: 'welcome', name: 'Welcome Email', subject: 'Welcome to Rentvine!', content: 'Thank you for your interest...' },
    { id: 'follow-up', name: 'Follow-up Email', subject: 'Following up on your inquiry', content: 'I wanted to follow up...' },
    { id: 'reminder', name: 'Reminder Email', subject: 'Don\'t forget about your appointment', content: 'This is a reminder...' },
    { id: 'custom', name: 'Custom Email', subject: '', content: '' }
  ]);

  const [groups] = useState([
    { id: 'hot-leads', name: 'Hot Leads', description: 'High priority leads' },
    { id: 'warm-leads', name: 'Warm Leads', description: 'Medium priority leads' },
    { id: 'cold-leads', name: 'Cold Leads', description: 'Low priority leads' },
    { id: 'vip-customers', name: 'VIP Customers', description: 'Premium customers' },
    { id: 'newsletter', name: 'Newsletter Subscribers', description: 'Marketing list' }
  ]);

  const [leadStages] = useState([
    { id: 'new', name: 'New Lead', color: '#3b82f6' },
    { id: 'contacted', name: 'Contacted', color: '#8b5cf6' },
    { id: 'qualified', name: 'Qualified', color: '#10b981' },
    { id: 'interested', name: 'Interested', color: '#f59e0b' },
    { id: 'proposal', name: 'Proposal Sent', color: '#ef4444' },
    { id: 'negotiation', name: 'Negotiation', color: '#6366f1' },
    { id: 'contract', name: 'Contract Review', color: '#ec4899' },
    { id: 'closed-won', name: 'Closed Won', color: '#059669' },
    { id: 'closed-lost', name: 'Closed Lost', color: '#dc2626' },
    { id: 'on-hold', name: 'On Hold', color: '#6b7280' },
    { id: 're-engaged', name: 'Re-engaged', color: '#f97316' }
  ]);

  // Available node types
  const nodeTypes = [
    {
      category: 'Triggers',
      color: '#3b82f6',
      nodes: [
        { id: 'new-lead', name: 'New Lead Added', icon: UserPlus, description: 'When someone fills out a lead form', inputs: 0, outputs: 1 },
        { id: 'lead-stage-change', name: 'Lead Stage Changed', icon: ArrowRight, description: 'When lead moves between stages', inputs: 0, outputs: 1 },
        { id: 'if-stage-is', name: 'If Stage Is', icon: CheckCircle, description: 'Check if lead is in specific stage', inputs: 0, outputs: 1 },
        { id: 'scheduled', name: 'Scheduled Time', icon: Clock, description: 'Run at specific times', inputs: 0, outputs: 1 },
        { id: 'form-submitted', name: 'Form Submitted', icon: FileText, description: 'When any form is completed', inputs: 0, outputs: 1 },
        { id: 'tour-scheduled', name: 'Tour Scheduled', icon: Calendar, description: 'When property tour is booked', inputs: 0, outputs: 1 }
      ]
    },
    {
      category: 'Actions',
      color: '#10b981',
      nodes: [
        { id: 'send-email', name: 'Send Email', icon: Mail, description: 'Send email to lead or team member', inputs: 1, outputs: 1 },
        { id: 'send-text', name: 'Send Text Message', icon: MessageSquare, description: 'Send SMS to lead', inputs: 1, outputs: 1 },
        { id: 'add-to-group', name: 'Add to Group', icon: Users, description: 'Add lead to a group or list', inputs: 1, outputs: 1 },
        { id: 'move-stage', name: 'Move to Stage', icon: ArrowRight, description: 'Move lead to different stage', inputs: 1, outputs: 1 },
        { id: 'create-task', name: 'Create Task', icon: CheckCircle, description: 'Create task for team member', inputs: 1, outputs: 1 },
        { id: 'make-call', name: 'Make Phone Call', icon: Phone, description: 'Call lead or team member', inputs: 1, outputs: 1 },
        { id: 'update-record', name: 'Update Record', icon: Database, description: 'Update lead information', inputs: 1, outputs: 1 }
      ]
    },
    {
      category: 'Conditions',
      color: '#f59e0b',
      nodes: [
        { id: 'if-email', name: 'If Email Contains', icon: Mail, description: 'Check if email contains specific text', inputs: 1, outputs: 2 },
        { id: 'if-stage', name: 'If Stage Is', icon: ArrowRight, description: 'Check current lead stage', inputs: 1, outputs: 2 },
        { id: 'if-group', name: 'If In Group', icon: Users, description: 'Check if lead is in specific group', inputs: 1, outputs: 2 },
        { id: 'if-time', name: 'If Time Is', icon: Clock, description: 'Check current time or date', inputs: 1, outputs: 2 }
      ]
    }
  ];

  // Handle keyboard events
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === 'Escape') {
        if (isConnecting) {
          setIsConnecting(false);
          setConnectionStart(null);
          setPreviewConnection(null);
        }
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [isConnecting]);

  // Handle canvas drag
  const handleCanvasMouseDown = useCallback((e) => {
    // Only start canvas panning if clicking on empty canvas (not on a node)
    if (e.target === canvasRef.current && !isNodeDragging) {
      setIsDragging(true);
      setDragStart({ x: e.clientX - canvasOffset.x, y: e.clientY - canvasOffset.y });
    }
  }, [isNodeDragging, canvasOffset]);

  // Handle canvas click to cancel connection
  const handleCanvasClick = useCallback((e) => {
    // If we're in connection mode and click on empty canvas, cancel the connection
    if (isConnecting && e.target === canvasRef.current) {
      setIsConnecting(false);
      setConnectionStart(null);
      setPreviewConnection(null);
    }
  }, [isConnecting]);

  const handleCanvasMouseMove = useCallback((e) => {
    if (isDragging && !isNodeDragging) {
      // Canvas panning
      setCanvasOffset({
        x: e.clientX - dragStart.x,
        y: e.clientY - dragStart.y
      });
    } else if (isNodeDragging && draggedNode) {
      // Handle node dragging
      const rect = canvasRef.current.getBoundingClientRect();
      const x = e.clientX - rect.left - canvasOffset.x - dragStart.x;
      const y = e.clientY - rect.top - canvasOffset.y - dragStart.y;

      setNodes(prevNodes => 
        prevNodes.map(node => 
          node.id === draggedNode ? { ...node, x, y } : node
        )
      );
    } else if (isConnecting && connectionStart) {
      // Show preview connection while dragging
      const rect = canvasRef.current.getBoundingClientRect();
      const startNode = nodes.find(n => n.id === connectionStart.nodeId);
      if (startNode) {
        const startX = startNode.x + startNode.width;
        const startY = startNode.y + startNode.height / 2;
        const endX = e.clientX - rect.left - canvasOffset.x;
        const endY = e.clientY - rect.top - canvasOffset.y;

        setPreviewConnection({
          startX,
          startY,
          endX,
          endY
        });
      }
    }
  }, [isDragging, isNodeDragging, draggedNode, dragStart, canvasOffset, isConnecting, connectionStart, nodes]);

  const handleCanvasMouseUp = useCallback(() => {
    setIsDragging(false);
    setIsNodeDragging(false);
    setDraggedNode(null);
    setPreviewConnection(null);
  }, []);

  // Node dragging is now handled by mouse events in the canvas

  // Handle node creation from palette
  const handlePaletteDragStart = (e, nodeType) => {
    // Store only the nodeType id, not the entire object with React components
    e.dataTransfer.setData('application/json', JSON.stringify({ id: nodeType.id }));
  };

  const handleCanvasDrop = (e) => {
    e.preventDefault();

    // Only handle drops from the palette (new nodes), not existing node movements
    const dragData = e.dataTransfer.getData('application/json');
    if (!dragData) return;

    try {
      const { id } = JSON.parse(dragData);
      if (!id) return;

      const nodeType = nodeTypes
        .flatMap(cat => cat.nodes)
        .find(nt => nt.id === id);

      if (!nodeType) return;

      const rect = canvasRef.current.getBoundingClientRect();
      const x = e.clientX - rect.left - canvasOffset.x;
      const y = e.clientY - rect.top - canvasOffset.y;

      const newNode = {
        id: `node-${Date.now()}`,
        type: nodeType.id,
        name: nodeType.name,
        icon: nodeType.icon,
        description: nodeType.description,
        inputs: nodeType.inputs,
        outputs: nodeType.outputs,
        x,
        y,
        width: 200,
        height: 80
      };

      setNodes([...nodes, newNode]);
    } catch (error) {
      console.error('Error parsing drag data:', error);
      return;
    }
  };

  // Handle connection creation
  const handleConnectionStart = (e, nodeId, outputIndex) => {
    e.stopPropagation();
    setIsConnecting(true);
    setConnectionStart({ nodeId, outputIndex });

    // Auto-cancel connection after 10 seconds if not completed
    setTimeout(() => {
      if (isConnecting) {
        setIsConnecting(false);
        setConnectionStart(null);
        setPreviewConnection(null);
      }
    }, 10000);
  };

  const handleConnectionEnd = (e, nodeId, inputIndex) => {
    e.stopPropagation();
    if (isConnecting && connectionStart && connectionStart.nodeId !== nodeId) {
      // Check if this is a valid connection (not connecting to the same node)
      const newConnection = {
        id: `conn-${Date.now()}`,
        from: connectionStart.nodeId,
        fromOutput: connectionStart.outputIndex,
        to: nodeId,
        toInput: inputIndex
      };
      setConnections([...connections, newConnection]);
    }
    // Always reset connection state, even if connection wasn't made
    setIsConnecting(false);
    setConnectionStart(null);
    setPreviewConnection(null);
  };

  // Handle connection deletion
  const handleDeleteConnection = (connectionId) => {
    setConnections(connections.filter(conn => conn.id !== connectionId));
  };

  // Render connection lines
  const renderConnections = () => {
    return connections.map(connection => {
      const fromNode = nodes.find(n => n.id === connection.from);
      const toNode = nodes.find(n => n.id === connection.to);

      if (!fromNode || !toNode) return null;

      const startX = fromNode.x + fromNode.width;
      const startY = fromNode.y + fromNode.height / 2;
      const endX = toNode.x;
      const endY = toNode.y + toNode.height / 2;

      const isHovered = hoveredConnection === connection.id;

      return (
        <svg
          key={connection.id}
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            pointerEvents: 'auto',
            zIndex: 1
          }}
        >
          <path
            d={`M ${startX} ${startY} Q ${(startX + endX) / 2} ${startY} ${(startX + endX) / 2} ${(startY + endY) / 2} Q ${(startX + endX) / 2} ${endY} ${endX} ${endY}`}
            stroke={isHovered ? "#ef4444" : "#3b82f6"}
            strokeWidth={isHovered ? "3" : "2"}
            fill="none"
            markerEnd="url(#arrowhead)"
            onMouseEnter={() => setHoveredConnection(connection.id)}
            onMouseLeave={() => setHoveredConnection(null)}
            onClick={(e) => {
              e.stopPropagation();
              handleDeleteConnection(connection.id);
            }}
            style={{ cursor: 'pointer' }}
          />
          {isHovered && (
            <circle
              cx={(startX + endX) / 2}
              cy={(startY + endY) / 2}
              r="8"
              fill="#ef4444"
              stroke="white"
              strokeWidth="2"
              style={{ cursor: 'pointer' }}
              onClick={(e) => {
                e.stopPropagation();
                handleDeleteConnection(connection.id);
              }}
            />
          )}
          <defs>
            <marker
              id="arrowhead"
              markerWidth="10"
              markerHeight="7"
              refX="9"
              refY="3.5"
              orient="auto"
            >
              <polygon
                points="0 0, 10 3.5, 0 7"
                fill="#3b82f6"
              />
            </marker>
          </defs>
        </svg>
      );
    });
  };

  // Render nodes
  const renderNodes = () => {
    return nodes.map(node => {
      const nodeType = nodeTypes
        .flatMap(cat => cat.nodes)
        .find(nt => nt.id === node.type);
      const color = nodeType ? nodeTypes.find(cat => cat.nodes.includes(nodeType))?.color : '#64748b';

      // Get the icon component from the nodeType, not the stored node
      const IconComponent = nodeType?.icon || CheckCircle;

      return (
        <div
          key={node.id}
          onDoubleClick={() => {
            const hasConfig = ['scheduled', 'send-email', 'add-to-group', 'move-stage', 'create-task'].includes(node.type);
            if (hasConfig) {
              configureNode(node.id);
            }
          }}
          style={{
            position: 'absolute',
            left: node.x,
            top: node.y,
            width: node.width,
            height: node.height,
            backgroundColor: 'white',
            border: `2px solid ${color}`,
            borderRadius: '8px',
            padding: '12px',
            cursor: isNodeDragging && draggedNode === node.id ? 'grabbing' : 'grab',
            zIndex: 2,
            boxShadow: isNodeDragging && draggedNode === node.id 
              ? '0 8px 25px rgba(0, 0, 0, 0.3)' 
              : '0 2px 8px rgba(0, 0, 0, 0.1)',
            transform: isNodeDragging && draggedNode === node.id ? 'scale(1.05)' : 'scale(1)',
            transition: 'all 0.2s ease',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            textAlign: 'center'
          }}
          onMouseDown={(e) => {
            e.stopPropagation();
            e.preventDefault();
            setDraggedNode(node.id);
            setIsNodeDragging(true);
            const rect = canvasRef.current.getBoundingClientRect();
            setDragStart({ 
              x: e.clientX - rect.left - canvasOffset.x - node.x, 
              y: e.clientY - rect.top - canvasOffset.y - node.y 
            });
          }}
        >
          <IconComponent size={24} color={color} style={{ marginBottom: '4px' }} />
          <div style={{ 
            fontSize: '12px', 
            fontWeight: '600', 
            color: 'rgb(68, 71, 69)',
            marginBottom: '2px'
          }}>
            {node.name}
          </div>
          <div style={{ 
            fontSize: '10px', 
            color: '#64748b',
            textAlign: 'center',
            lineHeight: '1.2'
          }}>
            {node.description}
          </div>
          {node.config && (
            <div style={{ 
              fontSize: '8px', 
              color: '#3b82f6',
              textAlign: 'center',
              marginTop: '2px',
              fontStyle: 'italic'
            }}>
              {node.type === 'scheduled' && `${node.config.scheduleType} at ${node.config.time}`}
              {node.type === 'send-email' && `${node.config.subject || 'No subject'}`}
              {node.type === 'add-to-group' && `${groups.find(g => g.id === node.config.groupId)?.name || 'Unknown'}`}
              {node.type === 'move-stage' && `${node.config.stage}`}
              {node.type === 'create-task' && `${node.config.title || 'Untitled'}`}
            </div>
          )}

          {/* Input connectors */}
          {node.inputs > 0 && (
            <div
              style={{
                position: 'absolute',
                left: '-6px',
                top: '50%',
                transform: 'translateY(-50%)',
                width: '12px',
                height: '12px',
                backgroundColor: hoveredConnectionPoint === `input-${node.id}-0` ? '#1d4ed8' : color,
                borderRadius: '50%',
                border: '2px solid white',
                cursor: 'crosshair',
                scale: hoveredConnectionPoint === `input-${node.id}-0` ? '1.2' : '1',
                transition: 'all 0.2s ease'
              }}
              onMouseUp={(e) => handleConnectionEnd(e, node.id, 0)}
              onMouseEnter={() => setHoveredConnectionPoint(`input-${node.id}-0`)}
              onMouseLeave={() => setHoveredConnectionPoint(null)}
            />
          )}

          {/* Output connectors */}
          {node.outputs > 0 && (
            <div
              style={{
                position: 'absolute',
                right: '-6px',
                top: '50%',
                transform: 'translateY(-50%)',
                width: '12px',
                height: '12px',
                backgroundColor: hoveredConnectionPoint === `output-${node.id}-0` ? '#1d4ed8' : color,
                borderRadius: '50%',
                border: '2px solid white',
                cursor: 'crosshair',
                scale: hoveredConnectionPoint === `output-${node.id}-0` ? '1.2' : '1',
                transition: 'all 0.2s ease'
              }}
              onMouseDown={(e) => handleConnectionStart(e, node.id, 0)}
              onMouseEnter={() => setHoveredConnectionPoint(`output-${node.id}-0`)}
              onMouseLeave={() => setHoveredConnectionPoint(null)}
            />
          )}

          {/* Configuration and Delete buttons */}
          <div style={{
            position: 'absolute',
            top: '-8px',
            right: '-8px',
            display: 'flex',
            gap: '4px'
          }}>
            {['scheduled', 'send-email', 'add-to-group', 'move-stage', 'create-task'].includes(node.type) && (
              <button
                onClick={() => configureNode(node.id)}
                style={{
                  width: '20px',
                  height: '20px',
                  backgroundColor: '#3b82f6',
                  color: 'white',
                  border: 'none',
                  borderRadius: '50%',
                  cursor: 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontSize: '10px'
                }}
                title="Configure"
              >
                <Settings size={10} />
              </button>
            )}
            <button
              onClick={() => {
                setNodes(nodes.filter(n => n.id !== node.id));
                setConnections(connections.filter(c => c.from !== node.id && c.to !== node.id));
              }}
              style={{
                width: '20px',
                height: '20px',
                backgroundColor: '#dc2626',
                color: 'white',
                border: 'none',
                borderRadius: '50%',
                cursor: 'pointer',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '12px'
              }}
              title="Delete"
            >
              <X size={12} />
            </button>
          </div>
        </div>
      );
    });
  };

  const configureNode = (nodeId) => {
    const node = nodes.find(n => n.id === nodeId);
    if (node) {
      setConfiguringNode(nodeId);
      setNodeConfig(node.config || {});
    }
  };

  const saveNodeConfig = () => {
    if (configuringNode) {
      setNodes(nodes.map(node => 
        node.id === configuringNode 
          ? { ...node, config: nodeConfig }
          : node
      ));
      setConfiguringNode(null);
      setNodeConfig({});
    }
  };

  const cancelNodeConfig = () => {
    setConfiguringNode(null);
    setNodeConfig({});
  };

  const createWorkflow = () => {
    if (workflowName.trim() && nodes.length > 0) {
      const newWorkflow = {
        id: editingWorkflowId || Date.now(),
        name: workflowName,
        status: 'inactive',
        description: workflowDescription,
        lastRun: null,
        runs: 0,
        nodes,
        connections,
        folderId: workflowFolder
      };

      if (onSave) {
        onSave(newWorkflow);
      } else {
        setWorkflows([...workflows, newWorkflow]);
      }

      setWorkflowName('');
      setWorkflowDescription('');
      setWorkflowFolder('system');
      setNodes([]);
      setConnections([]);
      setShowBuilder(false);
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'active': return <CheckCircle size={16} color="#10b981" />;
      case 'inactive': return <Pause size={16} color="#64748b" />;
      default: return <Clock size={16} color="#64748b" />;
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'active': return 'status-active';
      case 'inactive': return 'status-inactive';
      default: return 'status-inactive';
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Never';
    return new Date(dateString).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  // Configuration modal component
  const renderConfigModal = () => {
    if (!configuringNode) return null;

    const node = nodes.find(n => n.id === configuringNode);
    if (!node) return null;

    const renderConfigForm = () => {
      switch (node.type) {
        case 'scheduled':
          return (
            <div>
              <div style={{ marginBottom: '1rem' }}>
                <label className="form-label">Schedule Type</label>
                <select
                  value={nodeConfig.scheduleType || 'daily'}
                  onChange={(e) => setNodeConfig({...nodeConfig, scheduleType: e.target.value})}
                  className="form-input"
                >
                  <option value="daily">Daily</option>
                  <option value="weekly">Weekly</option>
                  <option value="monthly">Monthly</option>
                  <option value="custom">Custom Cron</option>
                </select>
              </div>
              <div style={{ marginBottom: '1rem' }}>
                <label className="form-label">Time</label>
                <input
                  type="time"
                  value={nodeConfig.time || '09:00'}
                  min="08:00"
                  max="22:00"
                  onChange={(e) => {
                    const time = e.target.value;
                    const [hours] = time.split(':').map(Number);
                    if (hours >= 8 && hours <= 22) {
                      setNodeConfig({...nodeConfig, time: time});
                    } else {
                      alert('Email sending is only allowed between 8:00 AM and 10:00 PM due to legal restrictions.');
                    }
                  }}
                  className="form-input"
                />
                <div style={{ fontSize: '0.75rem', color: '#64748b', marginTop: '0.25rem' }}>
                   Email sending restricted to 8:00 AM - 10:00 PM
                </div>
              </div>
              {nodeConfig.scheduleType === 'weekly' && (
                <div style={{ marginBottom: '1rem' }}>
                  <label className="form-label">Day of Week</label>
                  <select
                    value={nodeConfig.dayOfWeek || 'monday'}
                    onChange={(e) => setNodeConfig({...nodeConfig, dayOfWeek: e.target.value})}
                    className="form-input"
                  >
                    <option value="monday">Monday</option>
                    <option value="tuesday">Tuesday</option>
                    <option value="wednesday">Wednesday</option>
                    <option value="thursday">Thursday</option>
                    <option value="friday">Friday</option>
                    <option value="saturday">Saturday</option>
                    <option value="sunday">Sunday</option>
                  </select>
                </div>
              )}
            </div>
          );

        case 'send-email':
          return (
            <div>
              <div style={{ marginBottom: '1rem' }}>
                <label className="form-label">Email Template</label>
                <select
                  value={nodeConfig.template || 'welcome'}
                  onChange={(e) => setNodeConfig({...nodeConfig, template: e.target.value})}
                  className="form-input"
                >
                  {emailTemplates.map(template => (
                    <option key={template.id} value={template.id}>
                      {template.name}
                    </option>
                  ))}
                </select>
              </div>
              <div style={{ marginBottom: '1rem' }}>
                <label className="form-label">Subject</label>
                <input
                  type="text"
                  value={nodeConfig.subject || emailTemplates.find(t => t.id === (nodeConfig.template || 'welcome'))?.subject || ''}
                  onChange={(e) => setNodeConfig({...nodeConfig, subject: e.target.value})}
                  className="form-input"
                />
              </div>
              <div style={{ marginBottom: '1rem' }}>
                <label className="form-label">Recipient</label>
                <select
                  value={nodeConfig.recipient || 'lead'}
                  onChange={(e) => setNodeConfig({...nodeConfig, recipient: e.target.value})}
                  className="form-input"
                >
                  <option value="lead">Lead</option>
                  <option value="manager">Manager</option>
                  <option value="team">Team</option>
                  <option value="custom">Custom Email</option>
                </select>
              </div>
              <div style={{ marginBottom: '1rem' }}>
                <label className="form-label">Message Content</label>
                <textarea
                  value={nodeConfig.content || emailTemplates.find(t => t.id === (nodeConfig.template || 'welcome'))?.content || ''}
                  onChange={(e) => setNodeConfig({...nodeConfig, content: e.target.value})}
                  className="form-input"
                  rows={4}
                />
              </div>
            </div>
          );

        case 'add-to-group':
          return (
            <div>
              <div style={{ marginBottom: '1rem' }}>
                <label className="form-label">Select Group</label>
                <select
                  value={nodeConfig.groupId || 'hot-leads'}
                  onChange={(e) => setNodeConfig({...nodeConfig, groupId: e.target.value})}
                  className="form-input"
                >
                  {groups.map(group => (
                    <option key={group.id} value={group.id}>
                      {group.name} - {group.description}
                    </option>
                  ))}
                </select>
              </div>
              <div style={{ marginBottom: '1rem' }}>
                <label className="form-label">Action</label>
                <select
                  value={nodeConfig.action || 'add'}
                  onChange={(e) => setNodeConfig({...nodeConfig, action: e.target.value})}
                  className="form-input"
                >
                  <option value="add">Add to Group</option>
                  <option value="remove">Remove from Group</option>
                  <option value="move">Move to Group</option>
                </select>
              </div>
            </div>
          );

        case 'move-stage':
          return (
            <div>
              <div style={{ marginBottom: '1rem' }}>
                <label className="form-label">Target Stage</label>
                <select
                  value={nodeConfig.stage || 'Qualified'}
                  onChange={(e) => setNodeConfig({...nodeConfig, stage: e.target.value})}
                  className="form-input"
                >
                  {leadStages.map(stage => (
                    <option key={stage} value={stage}>
                      {stage}
                    </option>
                  ))}
                </select>
              </div>
              <div style={{ marginBottom: '1rem' }}>
                <label className="form-label">Condition</label>
                <select
                  value={nodeConfig.condition || 'immediate'}
                  onChange={(e) => setNodeConfig({...nodeConfig, condition: e.target.value})}
                  className="form-input"
                >
                  <option value="immediate">Immediately</option>
                  <option value="after-delay">After Delay</option>
                  <option value="if-condition">If Condition Met</option>
                </select>
              </div>
            </div>
          );

        case 'create-task':
          return (
            <div>
              <div style={{ marginBottom: '1rem' }}>
                <label className="form-label">Task Title</label>
                <input
                  type="text"
                  value={nodeConfig.title || ''}
                  onChange={(e) => setNodeConfig({...nodeConfig, title: e.target.value})}
                  className="form-input"
                  placeholder="Enter task title"
                />
              </div>
              <div style={{ marginBottom: '1rem' }}>
                <label className="form-label">Assign To</label>
                <select
                  value={nodeConfig.assignee || 'manager'}
                  onChange={(e) => setNodeConfig({...nodeConfig, assignee: e.target.value})}
                  className="form-input"
                >
                  <option value="manager">Manager</option>
                  <option value="sales-rep">Sales Rep</option>
                  <option value="team">Team</option>
                  <option value="specific">Specific User</option>
                </select>
              </div>
              <div style={{ marginBottom: '1rem' }}>
                <label className="form-label">Priority</label>
                <select
                  value={nodeConfig.priority || 'medium'}
                  onChange={(e) => setNodeConfig({...nodeConfig, priority: e.target.value})}
                  className="form-input"
                >
                  <option value="low">Low</option>
                  <option value="medium">Medium</option>
                  <option value="high">High</option>
                  <option value="urgent">Urgent</option>
                </select>
              </div>
              <div style={{ marginBottom: '1rem' }}>
                <label className="form-label">Due Date</label>
                <input
                  type="datetime-local"
                  value={nodeConfig.dueDate || ''}
                  onChange={(e) => setNodeConfig({...nodeConfig, dueDate: e.target.value})}
                  className="form-input"
                />
              </div>
            </div>
          );

        default:
          return <div>No configuration needed for this node.</div>;
      }
    };

    return (
      <div style={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundColor: 'rgba(0, 0, 0, 0.5)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        zIndex: 1000
      }}>
        <div style={{
          backgroundColor: 'white',
          padding: '2rem',
          borderRadius: '8px',
          minWidth: '500px',
          maxWidth: '600px',
          maxHeight: '80vh',
          overflow: 'auto',
          boxShadow: '0 20px 25px -5px rgba(0, 0, 0, 0.1)'
        }}>
          <h3 style={{ marginBottom: '1rem', color: '#1e293b' }}>
            Configure {node.name}
          </h3>
          {renderConfigForm()}
          <div style={{ display: 'flex', gap: '0.5rem', justifyContent: 'flex-end', marginTop: '1.5rem' }}>
            <button 
              className="btn btn-secondary"
              onClick={cancelNodeConfig}
            >
              Cancel
            </button>
            <button 
              className="btn btn-primary"
              onClick={saveNodeConfig}
            >
              Save Configuration
            </button>
          </div>
        </div>
      </div>
    );
  };

  if (showBuilder) {
    return (
      <div>
        {/* Breadcrumbs */}
        <div style={{ marginBottom: '1.5rem' }}>
          <span style={{ color: '#64748b', fontSize: '0.875rem' }}>Home / Marketing / Automations / Create Workflow</span>
        </div>

        {/* Page Title */}
        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center', 
          marginBottom: '2rem' 
        }}>
          <h1 style={{ 
            fontSize: '2rem', 
            fontWeight: '700', 
            color: 'rgb(68, 71, 69)' 
          }}>
            Visual Workflow Builder
          </h1>
          <div style={{ display: 'flex', gap: '0.5rem' }}>
            <button 
              className="btn btn-secondary"
              onClick={() => {
                setNodes([]);
                setConnections([]);
              }}
            >
              <RotateCcw size={16} style={{ marginRight: '4px' }} />
              Clear
            </button>
            <button 
              className="btn btn-secondary"
              onClick={() => {
                if (onClose) {
                  onClose();
                } else {
                  setShowBuilder(false);
                }
              }}
            >
              Cancel
            </button>
            <button 
              className="btn btn-primary"
              onClick={createWorkflow}
              disabled={!workflowName.trim() || nodes.length === 0}
            >
              <Save size={16} style={{ marginRight: '4px' }} />
              Save Workflow
            </button>
          </div>
        </div>

        {/* Workflow Details */}
        <div className="card" style={{ marginBottom: '2rem' }}>
          <div className="card-body">
            <h3 style={{ marginBottom: '1rem', color: 'rgb(68, 71, 69)' }}>Workflow Details</h3>
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: '1rem' }}>
              <div>
                <label className="form-label">Workflow Name</label>
                <input
                  type="text"
                  value={workflowName}
                  onChange={(e) => setWorkflowName(e.target.value)}
                  placeholder="e.g., New Lead Welcome"
                  className="form-input"
                />
              </div>
              <div>
                <label className="form-label">Description</label>
                <input
                  type="text"
                  value={workflowDescription}
                  onChange={(e) => setWorkflowDescription(e.target.value)}
                  placeholder="What does this workflow do?"
                  className="form-input"
                />
              </div>
              <div>
                <label className="form-label">Folder</label>
                <select
                  value={workflowFolder}
                  onChange={(e) => setWorkflowFolder(e.target.value)}
                  className="form-input"
                >
                  {folders.map(folder => (
                    <option key={folder.id} value={folder.id}>
                      {folder.name}
                    </option>
                  ))}
                </select>
              </div>
            </div>
          </div>
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: '250px 1fr', gap: '1rem', height: '600px' }}>
          {/* Node Palette */}
          <div className="card" style={{ height: '100%', overflow: 'auto' }}>
            <div className="card-header">
              <h3 className="card-title">Node Library</h3>
              <p className="card-subtitle">Drag nodes to canvas</p>
            </div>
            <div className="card-body" style={{ padding: '1rem' }}>
              {nodeTypes.map(category => (
                <div key={category.name} style={{ marginBottom: '1.5rem' }}>
                  <h4 style={{ 
                    fontSize: '0.875rem', 
                    fontWeight: '600', 
                    color: category.color,
                    marginBottom: '0.75rem',
                    textTransform: 'uppercase',
                    letterSpacing: '0.05em'
                  }}>
                    {category.category}
                  </h4>
                  <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
                    {category.nodes.map(nodeType => {
                      const IconComponent = nodeType.icon || CheckCircle;
                      return (
                        <div
                          key={nodeType.id}
                          draggable
                          onDragStart={(e) => handlePaletteDragStart(e, nodeType)}
                          style={{
                            display: 'flex',
                            alignItems: 'center',
                            gap: '0.75rem',
                            padding: '0.75rem',
                            backgroundColor: '#f8fafc',
                            border: '1px solid #e2e8f0',
                            borderRadius: '6px',
                            cursor: 'grab',
                            transition: 'all 0.2s ease'
                          }}
                          onMouseEnter={(e) => {
                            e.currentTarget.style.backgroundColor = '#f1f5f9';
                            e.currentTarget.style.borderColor = category.color;
                          }}
                          onMouseLeave={(e) => {
                            e.currentTarget.style.backgroundColor = '#f8fafc';
                            e.currentTarget.style.borderColor = '#e2e8f0';
                          }}
                        >
                          <IconComponent size={18} color={category.color} />
                          <div style={{ flex: 1 }}>
                            <div style={{ 
                              fontSize: '0.875rem', 
                              fontWeight: '500',
                              color: 'rgb(68, 71, 69)'
                            }}>
                              {nodeType.name}
                            </div>
                            <div style={{ 
                              fontSize: '0.75rem', 
                              color: '#64748b' 
                            }}>
                              {nodeType.description}
                            </div>
                          </div>
                        </div>
                      );
                    })}
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Canvas */}
          <div className="card" style={{ height: '100%', padding: 0, position: 'relative' }}>
            <div
              ref={canvasRef}
            onMouseDown={handleCanvasMouseDown}
            onMouseMove={handleCanvasMouseMove}
            onMouseUp={handleCanvasMouseUp}
            onClick={handleCanvasClick}
              onDrop={handleCanvasDrop}
              onDragOver={(e) => e.preventDefault()}
              style={{
                width: '100%',
                height: '100%',
                backgroundColor: '#f8fafc',
                backgroundImage: 'radial-gradient(circle, #e2e8f0 1px, transparent 1px)',
                backgroundSize: '20px 20px',
                position: 'relative',
                overflow: 'hidden',
                cursor: isDragging ? 'grabbing' : isNodeDragging ? 'grabbing' : 'grab'
              }}
            >
              {renderConnections()}
              {previewConnection && (
                <svg
                  style={{
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    width: '100%',
                    height: '100%',
                    pointerEvents: 'none',
                    zIndex: 1
                  }}
                >
                  <path
                    d={`M ${previewConnection.startX} ${previewConnection.startY} Q ${(previewConnection.startX + previewConnection.endX) / 2} ${previewConnection.startY} ${(previewConnection.startX + previewConnection.endX) / 2} ${(previewConnection.startY + previewConnection.endY) / 2} Q ${(previewConnection.startX + previewConnection.endX) / 2} ${previewConnection.endY} ${previewConnection.endX} ${previewConnection.endY}`}
                    stroke="#3b82f6"
                    strokeWidth="2"
                    strokeDasharray="5,5"
                    fill="none"
                    markerEnd="url(#arrowhead)"
                  />
                </svg>
              )}

              {isConnecting && (
                <div style={{ 
                  position: 'absolute',
                  top: '20px',
                  left: '50%',
                  transform: 'translateX(-50%)',
                  backgroundColor: '#3b82f6',
                  color: 'white',
                  padding: '8px 16px',
                  borderRadius: '6px',
                  fontSize: '0.9rem',
                  fontWeight: '500',
                  zIndex: 10,
                  boxShadow: '0 4px 12px rgba(59, 130, 246, 0.3)'
                }}>
                  Click on an input connection point to complete the connection
                  <span style={{ marginLeft: '8px', fontSize: '0.8rem' }}>or press ESC to cancel</span>
                </div>
              )}

              {renderNodes()}

              {nodes.length === 0 && !isConnecting && (
                <div style={{ 
                  position: 'absolute',
                  top: '50%',
                  left: '50%',
                  transform: 'translate(-50%, -50%)',
                  textAlign: 'center',
                  color: '#64748b'
                }}>
                  <Zap size={48} style={{ marginBottom: '1rem', opacity: 0.5 }} />
                  <div style={{ fontSize: '1.125rem', fontWeight: '500', marginBottom: '0.5rem' }}>
                    Start Building Your Workflow
                  </div>
                  <div>Drag nodes from the left to create your automation</div>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Configuration Modal */}
        {renderConfigModal()}
      </div>
    );
  }

  return (
    <div>
      {/* Breadcrumbs */}
      <div style={{ marginBottom: '1.5rem' }}>
        <span style={{ color: '#64748b', fontSize: '0.875rem' }}>Home / Marketing / Automations</span>
      </div>

      {/* Page Title */}
      <div style={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center', 
        marginBottom: '2rem' 
      }}>
        <h1 style={{ 
          fontSize: '2rem', 
          fontWeight: '700', 
          color: 'rgb(68, 71, 69)' 
        }}>
          Workflow Automations
        </h1>
        <button 
          className="btn btn-primary"
          onClick={() => setShowBuilder(true)}
        >
          <Plus size={16} style={{ marginRight: '4px' }} />
          {editingWorkflowId ? 'Edit Workflow' : 'Create Workflow'}
        </button>
      </div>

      {/* Workflows Grid */}
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(350px, 1fr))', gap: '1.5rem' }}>
        {workflows.map(workflow => (
          <div key={workflow.id} className="card">
            <div className="card-body">
              <div style={{ 
                display: 'flex', 
                justifyContent: 'space-between', 
                alignItems: 'flex-start',
                marginBottom: '1rem'
              }}>
                <div>
                  <h3 style={{ 
                    fontSize: '1.125rem', 
                    fontWeight: '600', 
                    color: 'rgb(68, 71, 69)',
                    marginBottom: '0.25rem'
                  }}>
                    {workflow.name}
                  </h3>
                  <p style={{ 
                    fontSize: '0.875rem', 
                    color: '#64748b',
                    marginBottom: '0.75rem'
                  }}>
                    {workflow.description}
                  </p>
                </div>
                <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                  {getStatusIcon(workflow.status)}
                  <span className={`automation-status ${getStatusColor(workflow.status)}`}>
                    {workflow.status}
                  </span>
                </div>
              </div>

              <div style={{ 
                display: 'flex', 
                justifyContent: 'space-between', 
                marginBottom: '1rem',
                fontSize: '0.875rem',
                color: '#64748b'
              }}>
                <span>Last Run: {formatDate(workflow.lastRun)}</span>
                <span>Total Runs: {workflow.runs}</span>
              </div>

              <div style={{ 
                display: 'flex', 
                gap: '0.5rem',
                justifyContent: 'flex-end'
              }}>
                <button className="btn btn-secondary btn-sm">
                  <Edit size={14} style={{ marginRight: '4px' }} />
                  Edit
                </button>
                <button className="btn btn-secondary btn-sm">
                  {workflow.status === 'active' ? <Pause size={14} /> : <Play size={14} />}
                </button>
                <button className="btn btn-secondary btn-sm">
                  <Copy size={14} />
                </button>
                <button className="btn btn-secondary btn-sm">
                  <Trash2 size={14} />
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {workflows.length === 0 && (
        <div style={{ 
          textAlign: 'center', 
          padding: '3rem', 
          color: '#64748b' 
        }}>
          <Zap size={48} style={{ marginBottom: '1rem', opacity: 0.5 }} />
          <p>No workflows created yet. Create your first automation to get started.</p>
        </div>
      )}
    </div>
  );
};

export default VisualWorkflowBuilder;
