import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import Sidebar from './components/Sidebar';
import Dashboard from './components/Dashboard';
import TasksDashboard from './components/TasksDashboard';
import Listings from './components/Listings';
import LeadToLeaseDashboard from './components/LeadToLeaseDashboard';
import SettingsTab from './components/SettingsTab';
import VisualWorkflowBuilder from './components/VisualWorkflowBuilder';
import AutomationsTab from './components/AutomationsTab';
import StrategicDashboard from './components/StrategicDashboard';
import OwnersMarketingDashboard from './components/OwnersMarketingDashboard';
import ExampleSite from './components/ExampleSite';
import PropertyManagementSite from './components/PropertyManagementSite';
import './components/PropertyManagementSite.css';

function App() {
  const [activeTab, setActiveTab] = useState('dashboard');

  // Check if we're on the example site route
  const isExampleSite = window.location.pathname === '/example-site';

  console.log('Current path:', window.location.pathname);
  console.log('Is example site:', isExampleSite);

  // If we're on the example site route, render the customer-facing site
  if (isExampleSite) {
    console.log('Rendering PropertyManagementSite');
    return (
      <div style={{ 
        width: '100%', 
        height: '100vh', 
        background: 'red', 
        color: 'white', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center',
        fontSize: '24px'
      }}>
        <div>
          <h1>EXAMPLE SITE IS WORKING!</h1>
          <p>This should be the customer-facing property management site</p>
          <button onClick={() => window.location.href = '/'}>
            Go Back to Portal
          </button>
        </div>
      </div>
    );
  }

  console.log('Rendering CRM Portal');

  // Otherwise, render the CRM portal

  const renderActiveTab = () => {
    switch (activeTab) {
      case 'dashboard':
        return <OwnersMarketingDashboard />;
      case 'properties':
        return <TasksDashboard />;
      case 'associations':
        return <TasksDashboard />;
      case 'listings':
        return <Listings />;
      case 'lead-to-lease':
        return <LeadToLeaseDashboard />;
      case 'campaigns':
        return <StrategicDashboard />;
      case 'analytics':
        return <TasksDashboard />;
      case 'automations':
        return <AutomationsTab />;
      case 'settings':
        return <SettingsTab />;
      case 'screening':
        return <TasksDashboard />;
      case 'leases':
        return <TasksDashboard />;
      case 'rentsign':
        return <TasksDashboard />;
      case 'accounting':
        return <TasksDashboard />;
      case 'contacts':
        return <TasksDashboard />;
      case 'maintenance':
        return <TasksDashboard />;
      case 'reports':
        return <TasksDashboard />;
      default:
        return <Dashboard />;
    }
  };

  return (
    <div className="app">
      <Sidebar activeTab={activeTab} setActiveTab={setActiveTab} />

      <div className="main-content-wrapper">
        <Header />
        <main className="main-content">
          {renderActiveTab()}
        </main>
      </div>
    </div>
  );
}

export default App;
